import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";




const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '80px',
  borderRadius: '5px'



}
const MenuUpdate = () => {
  const location = useLocation();

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    // resto: JSON.stringify(item.name),
    donner: JSON.parseInt(location.state.ligne.itemm.deal_id),
    item1: JSON.stringify(location.state.ligne.itemm.imageurl),
    item2: JSON.stringify(location.state.ligne.itemm.discount),
    item3: JSON.parseFloat(location.state.ligne.itemm.PriceAfterDiscount),
    item4: JSON.parseFloat(location.state.ligne.itemm.PriceBeforeDiscount),
    item5: JSON.stringify(location.state.ligne.itemm.description),
    item6: JSON.stringify(location.state.ligne.itemm.deal_description),
    item7: JSON.stringify(location.state.ligne.itemm.startingdate),
    item8: JSON.stringify(location.state.ligne.itemm.expirydate),




  });


  const handleClick = () => {
    setOpen(true);
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }

  console.log("les items de la page dernière est :" + data.donner);
  const [basketdata, setBasketdata] = React.useState({
    name: "",
    deal_id: "",
    imageurl: "",
    discount: "",
    PriceAfterDiscount: "",
    PriceBeforeDiscount: "",
    description: "",
    deal_description: "",
    startingdate: "",
    expirydate: "",


  });
  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      name: e
    })
  }
  //  }
  const dealidchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_id: e
    })
  }
  const imagechange = (e) => {
    setBasketdata({
      ...basketdata,
      imageurl: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const beforedischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceBeforeDiscount: e
    })
  }
  const afterdischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }


  const dealdescriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_description: e
    })
  }

  const startingdatechange = (e) => {
    setBasketdata({
      ...basketdata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setBasketdata({
      ...basketdata,
      expirydate: e
    })
  }

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const global = (id) => {
    handleEdit(id);
    handleClick();
    handleHistory();



  };
  const [open, setOpen] = React.useState(false);



  const handleEdit = (id) => {


    axios.put(`http://192.168.22.78:5000/api/entreroffres/update/${id}`, {
      name: basketdata.name,
      deal_id:parseInt(basketdata.deal_id) ,
      imageurl: basketdata.imageurl,
      discount: basketdata.discount,
      PriceAfterDiscount: parseFloat(basketdata.PriceAfterDiscount),
      PriceBeforeDiscount: parseFloat(basketdata.PriceBeforeDiscount),
      discount: basketdata.discount,
      description: basketdata.description,
      deal_description: basketdata.deal_description,
      startingdate: basketdata.startingdate,
      expirydate: basketdata.expirydate,

    })

      .then(() => {
        console.log("update item" + nom);

      })
      .catch(error => {
        console.log('updating error is :' + error);
      });
  };


  return (
    <div >

      <div style={Title1}>
        <h2> Update Menu</h2>
      </div>
      <div style={Field}>
       
        <TextField
          label="nom" defaultValue={data.resto} onChange={e => namechange(e.target.value)} />
        <TextField
          label="deal_id" defaultValue={data.donner} onChange={e => dealidchange(e.target.value)} />
        <TextField
          label="url de l'image" defaultValue={data.item1} onChange={e => imagechange(e.target.value)} />
        <TextField
          label="discount" defaultValue={data.item2} onChange={e => discountchange(e.target.value)} />
        <TextField
          label="price before" defaultValue={data.item3} onChange={e => beforedischange(e.target.value)} />
        <TextField
          label="price After" defaultValue={data.item4} onChange={e => afterdischange(e.target.value)} />
        <TextField
          label="decription" defaultValue={data.item5} onChange={e => descriptionchange(e.target.value)} />

        <TextField
          label="deal decription" defaultValue={data.item6} onChange={e => dealdescriptionchange(e.target.value)} />
        <TextField
          label="starting date" defaultValue={data.item7} onChange={e => startingdatechange(e.target.value)} />
        <TextField
          label="expired date" defaultValue={data.item8} onChange={e => expirydatechange(e.target.value)} />

      </div>



      <div style={buttonstyle}>
        <Button variant="outline-success" onClick={() => global(data.donner)}  >confirmer</Button>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            partner is successfully updated!
</Alert>
        </Snackbar>
      </div>

    </div>

  )

};


export default MenuUpdate;
