import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";

import { withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import axios from 'axios';
import Partnersadd from './Partnersadd';
import PartnersUpdate from './PartnersUpdate';

const Partners = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [q, setQ] = useState("");

  const [visible, setVisible] = React.useState(false);

  const showDialog = () => {
    setVisible(true);
  };
  const handleCancel = () => {
    setVisible(false);
  };
  const CustomTableCell = withStyles(theme => ({
    body: {
      borderBottomColor: 'transparent',
      paddingTop: theme.spacing(1),
      paddingBottom: theme.spacing(1)
    }
  }))(TableCell);

  useEffect(() => {
    setTimeout(async () => {

      getData()

      // let restaurantid;
      // restaurantid = null;
      // try {
      //   restaurantid = await AsyncStorage.getItem('restaurant_id');
      // } catch (e) {
      //   console.log(e);
      // }

      // console.log('focused section');
    }



      , 500);


  }, [])

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/restaurants';
    let user;
    user = [];
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };



  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/restaurants/${id}`)
      .then(res => {
        console.log(id)
        if (res.data == 'partner Deleted!') {
          // handleCancel()
          getData()

        }
      })
      .catch((err) => {
        console.log(err);
      })
  }
  // const searchFilterFunction = (text) => {
  //   const newData = data.temp.filter((item) => {
  //     const itemData = `${item.nom.toUpperCase()}`;
  //     const textData = text.toUpperCase();
  //     return itemData.indexOf(textData) > -1;
  //   });
  //   setData({
  //     ...data,
  //     data: newData
  //   })
  // };
  const addPartner = () => {
    let partnerItems = localStorage.getItem('partneritem');

    console.log(partnerItems)
    axios.post('https://api.foodealzapi.com/restaurants', partnerItems).then((res) => {

      console.log(res.data)
    })
      .catch(err => Toast.show("Veuillez vÃ©rifier la reservation!"));
    partnerItems.map((item) => {

    })
    sessioncStorage.setItem('partnerItems', JSON.stringify([]));

    setData({
      ...data,

    })
    // navigation.jumpTo('Mes Couffins')
    // navigation.goBack()
  }
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: 'white',
    marginLeft: '20px',
    marginTop: '20px',
    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'
  }

  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '110px',
    borderRadius: '5px'
  }
  return (
    <div>
      <div style={Title1}>
        <h2>Partners</h2>

        <div style={buttonstyle}>

          <Button variant="outline-success" onClick={showDialog()}  >add partner </Button>
          <div >

            <Dialog style={Title1} aria-labelledby="form-dialog-title">
              <DialogTitle >Enter new Fields</DialogTitle>
              <DialogContent>
                <div style={Field}>
                  <TextField
                    label="Enter partnerName" />

                  <TextField style={{ marginLeft: '50px' }}
                    label="Enter partnerMail" />

                </div >


                <div style={Field}>
                  <TextField
                    label="Enter phone" />


                  <TextField style={{ marginLeft: '50px' }}
                    label="Enter city" />
                </div>

                <div >
                  <TextField style={{ marginTop: '20px', marginLeft: '25px' }}
                    label="Enter password" />
                </div>

              </DialogContent>
              <DialogActions>
                <Button onClick={handleCancel()} color="primary">
                  Cancel
        </Button>
                <Button onClick={addPartner()} color="primary">
                  valider
        </Button>
              </DialogActions>
            </Dialog>



          </div>
        </div>

      </div>
      <div style={Search1} >
        <input type="text" placeholder=" Search Here..." value={q} onChange={(e) => setQ(e.target.value)} />
      </div>
      <div style={item}>

        <Table>
          <TableHead>
            <TableRow>
              <CustomTableCell style={{ alignItems: 'center' }} >partnerId</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerName</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerMail</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerPhone</CustomTableCell>

              <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerPassword</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerCity</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right"> </CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right"> </CustomTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.data.map((item, restaurant_id) => (
              <TableRow key={restaurant_id}>
                <CustomTableCell>

                  {item.restaurant_id}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.name}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.mail}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.phone}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.password}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.address}
                </CustomTableCell>
                <CustomTableCell align="right">
                  <Link to='/PartnersUpdate'>

                    <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success">
                      <EditIcon />
                    </Button>
                  </Link>
                </CustomTableCell>
                <CustomTableCell align="right">
                  <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" onClick={() => this.handleRemove(restaurant_id)}>
                    <ClearIcon />
                  </Button>
                </CustomTableCell>

              </TableRow>
            ))}
          </TableBody>
        </Table>


      </div>
      <div>

      </div>

    </div>
  )
}
export default Partners;
