import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
// import { useForm } from 'react-hook-form';
// import { yupResolver } from '@hookform/resolvers/yup';
// import * as yup from 'yup';
import { withStyles, makeStyles } from '@material-ui/core/styles';




// let schema = yup.object().shape({
//     username: yup.string().required('please enter username'),
//     email: yup.string().email().required('email invallid'),
//     password: yup.string().min(4).max(8).required('password nvalid'),

// });



const TestValide = () => {

    // const { register, handleSubmit, error } = useForm({
    //     resolver: yupResolver(schema),
    // });

    const [data, setData] = React.useState({
    
        error: '',
        data: [],
        temp: [],
        error: null,
        fields: [],
        errors: [],
        username:'',
        email:'',
        phone:'',
        password:''
       
      });
       const [formIsValid, setFormIsValid] = React.useState(false);
     
     const handleValidation =() =>{
         const fields= data.fields;
         const errors={};
         let formIsValid = true;

            //Name
            if(!fields["username"]){
               formIsValid = false;
               errors["username"] = "Cannot be empty";
            }     
            if(typeof fields["username"] !== "undefined"){
               if(!fields["username"].match(/^[a-zA-Z]+$/)){
                 formIsValid = false;
                  errors["username"] = "Only letters";
               }        
            }

              //Email
            if(!fields["email"]){
               formIsValid = false;
               errors["email"] = "Cannot be empty";
            }
      
            if(typeof fields["email"] !== "undefined"){
               const lastAtPos = fields["email"].lastIndexOf('@');
               const lastDotPos = fields["email"].lastIndexOf('.');

               if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') == -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
                 formIsValid = false;
                  errors["email"] = "Email is not valid";
                }
           }  
           //Phone
            if(!fields["phone"]){
               formIsValid = false;
               errors["phone"] = "Cannot be empty";
            }     
            if(typeof fields["phone"] !== "undefined"){
               if(!fields["phone"].match(/^[0-9]+$/)){
               formIsValid = false;
                  errors["phone"] = "Only numbers";
               }        
            }
           


            setData({
          ...data,
          errors: errors,
        });

        return formIsValid;


     };

     const contactSubmit = (e) => {
        //  e.preventDefault()
         if(handleValidation()){
               alert("Form submitted");
            }else{
               alert("Form has errors.")
            }

     }

     const handleChange =(field, e) =>{
         let fields = data.fields;
         fields[field]=e.target.value;
           setData({
          ...data,
          fields: fields,
        });
         

     }


        
    
    //   const usernamechange = (e) => {
    //     setData({
    //       ...data,
    //       username: e
    //     })
    //   }

    //   const emailchange = (e) => {
    //     setData({
    //       ...data,
    //       email: e
    //     })
    //   }

    //   const passwordchange = (e) => {
    //     setData({
    //       ...data,
    //       password: e
    //     })
    //   }

    // const submitForm = () => {
    //     console.log(data.username);
    //     console.log(data.email);
    //     console.log(data.password);
    // };
    

    return (
        <div>
            <h2>Sign In</h2>
             <form name="contactform" className="contactform" onSubmit={contactSubmit()}>
                        <div className="col-md-6">
                          <fieldset>
                               <input ref="username" type="text" size="30" placeholder="username" onChange={(e)=> handleChange(e,"name")} value={data.fields["username"]}/>
                               <span style={{color: "red"}}>{data.errors["username"]}</span>
                              <br/>
                             <input refs="email" type="text" size="30" placeholder="Email" onChange={(e)=>handleChange(e,"email")} value={data.fields["email"]}/>
                             <span style={{color: "red"}}>{data.errors["email"]}</span>
                             <br/>
                             <input refs="phone" type="text" size="30" placeholder="Phone" onChange={(e)=>handleChange(e,"phone")} value={data.fields["phone"]}/>
                              <span style={{color: "red"}}>{data.errors["phone"]}</span>
                             <br/>
                             {/* <input refs="address" type="text" size="30" placeholder="Address" onChange={this.handleChange.bind(this, "address")} value={this.state.fields["address"]}/>
                             <br/> */}
                         </fieldset>
                      </div>
          
                  </form>
                   <Button   > submit</Button> 
                   
            {/* <form>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>username:</p>
                    <input type="text" name="username" onChange={e => usernamechange(e.target.value)} placeholder="enter username"  />
                    {/* <p>{error.username?.message}</p> */}
                    {/* <TextField style={{ marginLeft: '20px', width: '300px', marginTop: '25px', padding: '15px' }}
                        name="username" variant="filled"  /> */}
{/*                     
                </div>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>email :</p>
                    <input type="text" name="email" placeholder="enter email" onChange={e => emailchange(e.target.value)}   /> */}
                    {/* <p>{error.email?.message}</p> */}
                    {/* <TextField style={{ width: '300px', marginTop: '25px', padding: '15px' }}
                        name="email" variant="filled"  /> */}
                    
                {/* </div>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>password :</p>
                    <input type="text" name="password" placeholder="enter password" onChange={e => passwordchange(e.target.value)}  /> */}
                    {/* <p>{error.password?.message}</p> */}
                    {/* <TextField style={{ width: '300px', marginTop: '25px', padding: '15px' }}
                        name="password" variant="filled"  /> */}
                  
                {/* </div>

                <div >
                    
                    <input type="submit" id="submit"   /> */}
                    {/* <TextField style={{ width: '300px', marginTop: '25px', padding: '15px' }}
                        name="password" variant="filled"  /> */}
                  
                {/* </div>

            </form>
            <Button> submit</Button> */} 
        </div>

    );
}
export default TestValide;
