import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { Link } from "react-router-dom";
import React from "react";
import TextField from "@material-ui/core/TextField";
import { AuthContext } from '../../context';
import Typography from "@material-ui/core/Typography";
import classNames from "classnames";
import { makeStyles } from "@material-ui/core/styles";
import Alert from "@material-ui/lab/Alert";
import axios from 'axios';

const useStyles = makeStyles(theme => ({
  card: {
    overflow: "visible"
  },
  session: {
    position: "relative",
    zIndex: 4000,
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column"
  },
  background: {
    backgroundColor: theme.palette.primary.main
  },
  content: {
    padding: `40px ${theme.spacing(1)}px`,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    flex: "1 0 auto",
    flexDirection: "column",
    minHeight: "100%",
    textAlign: "center"
  },
  wrapper: {
    flex: "none",
    maxWidth: "400px",
    width: "100%",
    margin: "0 auto"
  },
  fullWidth: {
    width: "100%"
  },
  logo: {
    display: "flex",
    flexDirection: "column"
  }
}));

const Signin = () => {
  const classes = useStyles();
  const [data, setData] = React.useState({

    error: '',
    error: null,
    search: null,
    usename:"",
    password:"",
    mail:""


  });
  // const { signIn } = React.useContext(AuthContext);

  const controle = async () => {
    // let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    // if (data.mail == "" || data.password == "") {
    //   Toast.show('Il faut remplir tous les champs')

    // }
    // else if (data.password.length < 8) {
    //   Toast.show('Mot de passe est trop court')

    // }
    // else if (reg.test(data.mail) === false) {
    //   Toast.show('Le format de mail est invalide')

    // }
    // else {
      axios
        .post(`https://api.foodealzapi.com/Admin/login`, {
          mail: data.username,
          password: data.password,
        })
        .then(res => {
 console.log(res)          

        })
        .catch(err =>console.log("Veuillez vérifier votre connexion!"));
    // }

  }

  return (
    <div className={classNames(classes.session, classes.background)}>
      <div className={classes.content}>
        <div className={classes.wrapper}>
          <Card>
            <CardContent>
              <form>
                <div
                  className={classNames(classes.logo, `text-xs-center pb-xs`)}
                >
                  <img
                    src={`${process.env.PUBLIC_URL}/static/images/foodealz1.png`}
                    alt=""
                    className="block"
                  />

                </div>
                <TextField
                  id="username"
                  label="Username"
                  className={classes.textField}
                  value={data.username}
                  onChange={event => {  setData({ ...data, username: event.target.value }) }}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  id="password"
                  label="Password"
                  className={classes.textField}
                  value={data.password}
                  onChange={event => {  setData({ ...data, password: event.target.value }) }}
                  type="password"
                  fullWidth
                  margin="normal"
                />

                 {/* <Link to="/Dashbord"> */}
                <Button
                  variant="contained"
                  color="primary"
                  fullWidth
                  onClick={()=> {controle()}}
                >
                  Login
                </Button>
                {/* </Link> */}

              </form>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Signin;
