import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
  fontSize: '50px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '150px',
  borderRadius: '5px'



}
const OffresUpdate = (itemupdate, props) => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    donner: JSON.stringify(location.state.ligne.item.id)
  });
  const location = useLocation();

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  console.log("les items de la page dernière est :"+ data.donner);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }
  // const [visible, setVisible] = React.useState(false);
  // const handleCancel = () => {
  //     setVisible(false);
  // };
  const [offredata, setOffredata] = React.useState({
    id: "",
    quantity: "",
    nbre_redeemed_deal: "",
    startingdate: "",
    expirydate: "",
    startingdate_hours: "",
    expirydate_hours: "",
    active: "",
    restaurant_id: "",
    deal_id: ""



  });

  const idchange = (e) => {
    setOffredata({
      ...offredata,
      id: e
    })
  }

  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const startingdatechange = (e) => {
    setOffredata({
      ...offredata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setOffredata({
      ...offredata,
      expirydate: e
    })
  }
  const startingdate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      startingdate_hours: e
    })
  }
  const expirydate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      expirydate_hours: e
    })
  }
  const activechange = (e) => {
    setOffredata({
      ...offredata,
      active: e
    })
  }
  const restaurant_idchange = (e) => {
    setOffredata({
      ...offredata,
      restaurant_id: e
    })
  }
  const deal_idchange = (e) => {
    setOffredata({
      ...offredata,
      deal_id: e
    })
  }

  const handleEdit = (id) => {

    axios.put(`https://api.foodealzapi.com/deals/${id}`, {
      id: offredata.id,
      quantity: offredata.quantity,
      nbre_redeemed_deal: offredata.nbre_redeemed_deal,
      startingdate: offredata.startingdate,
      expirydate: offredata.expirydate,
      startingdate_hours: offredata.startingdate_hours,
      expirydate_hours: offredata.expirydate_hours,
      active: offredata.active,
      restaurant_id: offredata.restaurant_id,
      deal_id: offredata.deal_id,
    })
      .then(() => {
        console.log("done")
      })
      .catch(error => {
        console.log(error);
      });
  }
  const global = (id) => {
    handleEdit(id);
    handleClick();
    handleHistory();
  }
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  console.log(location.state)

  return (
    <div style={{backgroundColor: "#FF9F54"}}>
      <div >
        <div>
          <ArrowBackIosIcon style={{ color: "#008037", fontSize: 40, marginTop: "20px", marginLeft: "20px" }} onClick={() => { handleHistory() }} />
        </div>
        <h2 style={Title1} >Update deal</h2>

        <div style={Field}>
          <p style={{fontSize: '20px'}}>id :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="id" onChange={e => idchange(e.target.value)} variant="filled"/>
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>quantity :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="quantity" onChange={e => qtchange(e.target.value)} variant="filled" />
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>quantity sold :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="quantity sold" onChange={e => offresoldchange(e.target.value)} variant="filled"/>
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}> starting date :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="starting date" onChange={e => startingdatechange(e.target.value)} variant="filled"/>
        </div>
        <div style={Field}>
        <p style={{fontSize: '20px'}}>expired date :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="expiry date" onChange={e => expirydatechange(e.target.value)} variant="filled"/>
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>starting hour :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="starting hour" onChange={e => startingdate_hourschange(e.target.value)} variant="filled"/>
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>expired hour :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="expiry hour" onChange={e => expirydate_hourschange(e.target.value)} variant="filled" />
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>active or not :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="active or not" onChange={e => activechange(e.target.value)} variant="filled"/>
            </div>
            <div style={Field}>
            <p style={{fontSize: '20px'}}>restaurant id :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="restaurant id" onChange={e => restaurant_idchange(e.target.value)} variant="filled"/>
        </div>
        <div style={Field}>
        <p style={{fontSize: '20px'}}>deal id :</p>
          <TextField style={{ marginLeft: '100px' }}
            label="deal id" onChange={e => deal_idchange(e.target.value)} variant="filled"/>
        </div>


        <Button style={buttonstyle} onClick={() => global(data.donner)} >
          confirmer
                    </Button>
        <Snackbar open={open} autoHideDuration={2000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            deal is successfully updated!
</Alert>
        </Snackbar>

      </div>
    </div>
  )

};

export default OffresUpdate;
