import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import DialogActions from '@material-ui/core/DialogActions';
import MuiAlert from '@material-ui/lab/Alert';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import { getOverlappingDaysInIntervals } from 'date-fns';




const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '100px',
  width: '300px',
  marginTop: '25px',
  padding: '15px',
}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}

const useStyles = makeStyles((theme) => ({
  ligne: {
    display: 'flex',
    flexDirection: 'row',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 200,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const Dealadd = (item) => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    invenduselected: '',
    Invendulist:[],
    InvenduChoisie:[],
    error: null,
    partnerId: "",
    restaurant:null,
    name: "",
    typePar: "",
    commission_ratePar: "",
    discountPar: "",
    startingdate:"",
    expireddate:""
  });

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);
  const [open1, setOpen1] = React.useState(false);



  const handleChange = (event) => {
    const name = event.target.name;
    setData({
      ...data,
      [name]: event.target.value,
    });
  };
  const [dealdata, setDealdata] = React.useState({
    imageurl: "",
    PriceAfterDiscount: "",
    PriceBeforeDiscount: "",
    description: "",
    deal_description: "",
    restaurant_id: "",

  });

  const imagechange = (e) => {
    setDealdata({
      ...dealdata,
      imageurl: e
    })
  }

  const afterchange = (e) => {
    setDealdata({
      ...dealdata,
      PriceAfterDiscount: e
    })
  }
  const beforechange = (e) => {
    setDealdata({
      ...dealdata,
      PriceBeforeDiscount: e
    })
  }

  const descriptionchange = (e) => {
    setDealdata({
      ...dealdata,
      description: e
    })
  }
  const dealdescrichange = (e) => {
    setDealdata({
      ...dealdata,
      deal_description: e
    })
  }
  const restoidchange = (e) => {
    setDealdata({
      ...dealdata,
      restaurant_id: e
    })
  }
 
  const changeidpartner = (e) => {
    setData({
      ...data,
      restaurant: e
    })
    getInvendu(parseInt(e))

  }

  const ChangeInvendu = (e) => {
    let newdata = data.Invendulist.filter(item =>{return item.nom == e})
    console.log(newdata.imageurl)
    setData({
      ...data,
      invenduselected: e,
      InvenduChoisie: newdata
    })
    
  }

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/activedeals';
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };
  const getInvendu = async (id) => {
    const url = `https://api.foodealzapi.com/Invendus/${id}`;
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        setData({
          ...data,
          Invendulist: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  const addDeal = () => {
    axios.post('https://api.foodealzapi.com/deals/dealcreate', {
      restaurant_id: parseInt(data.restaurant),
      imageurl: dealdata.imageurl,
      discount:data.discountPar,
      PriceAfterDiscount: dealdata.PriceAfterDiscount,
      PriceBeforeDiscount: dealdata.PriceBeforeDiscount,
      description: dealdata.description,
      deal_description: dealdata.deal_description,
      startingdate: data.startingdate,
      expireddate: data.expireddate,
     
      
    })
      .then(() => {
        console.log("added successfully");
      })
      .catch(error => {
        console.log(" problem in posting " + error)

      });

  }
  
    const handleClick = () => {
      setOpen1(true);
    };
    


  const handleClose1 = () => {
    setOpen(false);

  };
  const global = () => {
    addDeal();
   handleClick();
   handleClose1();

  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen1(false);
  };
  const classes = useStyles();

  return (
    <div >
      <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} variant="outline-success" onClick={() => setOpen(true)} >add deal </Button>
      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Enter new partner</DialogTitle>
        <DialogContent>
        <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}>restaurant_id :</p>
            <FormControl variant="filled" className={classes.formControl}>
              <InputLabel htmlFor="filled-age-native-simple">partner id</InputLabel>
              <Select
                native
                value={data.restaurant}
                onChange={val => {changeidpartner(val.target.value)}}
                inputProps={{
                  name: 'partnerId',
                  id: 'filled-age-native-simple3',
                }}
              >
                <option aria-label="None" value="" />
                <option value={1}>1</option>
                <option value={2}>2</option>
                <option value={3}>3</option>
                <option value={4}>4</option>
                <option value={5}>5</option>
                <option value={6}>6</option>
                <option value={7}>7</option>
                <option value={8}>8</option>
                
              </Select>
            </FormControl>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}>invendus list :</p>
            <FormControl variant="filled" className={classes.formControl}>
              <Select
                native
                value={data.invenduselected}
                onChange={val => {ChangeInvendu(val.target.value)}}
                inputProps={{
                  name: 'partnerId',
                  id: 'filled-age-native-simple3',
                }}
              >
                 <option aria-label="None" value="" />
                {
                  data.Invendulist.map(item => {
                    return <option value={item.nom}>{item.nom}</option>
                  })
                }
                
              </Select>
            </FormControl>
          </div>
           <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>image url :</p>
            <TextField style={Field}
              label="image url" onChange={e => imagechange(data.InvenduChoisie.imageurl)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}>discount :</p>
            <FormControl variant="filled" className={classes.formControl}>
              <InputLabel htmlFor="filled-age-native-simple">discount</InputLabel>
              <Select
                native
                value={data.discountPar}
                onChange={handleChange}
                inputProps={{
                  name: 'discountPar',
                  id: 'filled-age-native-simple3',
                }}
              >
                <option aria-label="None" value="" />
                <option value={"10%"}>10%</option>
                <option value={"20%"}>20%</option>
                <option value={"30%"}>30%</option>
                <option value={"40%"}>40%</option>
                <option value={"50%"}>50%</option>
                <option value={"60%"}>60%</option>
                <option value={"70%"}>70%</option>
                <option value={"80%"}>80%</option>
                <option value={"90%"}>90%</option>
              </Select>
            </FormControl>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>price after:</p>
            <TextField style={Field}
              label="price after" onChange={e => afterchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>price before :</p>
            <TextField style={Field}
              label="price before" onChange={e => beforechange(e.target.value)} variant="filled" />
          </div>

          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>description :</p>
            <TextField style={Field}
              label="description" onChange={e => descriptionchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>deal description :</p>
            <TextField style={Field}
              label="deal description" onChange={e => dealdiscrichange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>starting date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date1"
             
                label="starting date"
                type="datetime-local"
                name="starting date"
                value={data.startingdate}
                onChange={event => { console.log(event.target.value),setData({...data,startingdate:event.target.value })}}
                
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          <div className={classes.ligne} >
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>expired date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date2"
                label="expired date"
                type="datetime-local"
                name="expired date"
                defaultValue={data.expireddate}
                onChange={event => { console.log(event.target.value),setData({...data,expireddate:event.target.value })}}
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          
          

        </DialogContent>
        <DialogActions>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={handleClose1} >
            No
</Button>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={() => global()} > valider </Button>

        </DialogActions>
      </Dialog>
      <Snackbar open={open1} autoHideDuration={1000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        deal is successfully added!
    </Alert>
    </Snackbar>














    </div>

  )

};

export default Dealadd;
