import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Button, ThemeProvider } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';

import { Dialog } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import config from '../config';



const DialogAdd = (item) => {

  const [open, setOpen] = React.useState(false);
  const [open1, setOpen1] = React.useState(false);
  const [data, setData] = React.useState({
        error: '',
        data: [],
        temp: [],
        invenduselected: '',
        Invendulist:[],
        InvenduChoisie:[],
        error: null,
        partnerId: "",
        restaurant:null,
        name: "",
        typePar: "",
        commission_ratePar: "",
        discountPar: "",
        startingdate:"",
        expireddate:""
      });
    

  const [dealdata, setDealdata] = React.useState({
    imageurl: "",
    discount: "",
    PriceAfterDiscount: null,
    PriceBeforeDiscount: null,
    description: "",
    deal_description: "",
    restaurant_id: "",

  });

  const imagechange = (e) => {
    setDealdata({
      ...dealdata,
      imageurl: e
    })
  }
  const discountchange = (e) => {
    setDealdata({
      ...dealdata,
      discount: e
    })
  }

  const afterchange = (e) => {
    setDealdata({
      ...dealdata,
      PriceAfterDiscount: e
    })
  }
  const beforechange = (e) => {
    setDealdata({
      ...dealdata,
      PriceBeforeDiscount: e
    })
  }

  const descriptionchange = (e) => {
    setDealdata({
      ...dealdata,
      description: e
    })
  }
  const dealdescrichange = (e) => {
    setDealdata({
      ...dealdata,
      deal_description: e
    })
  }
 
 
  const changeidpartner = (e) => {
    setData({
      ...data,
      restaurant: e
    })
    getInvendu(parseInt(e))

  }

  const ChangeInvendu = (e) => {
    let newdata = data.Invendulist.filter(item =>{return item.nom  == e})
    setData({
      ...data,
      invenduselected: e,
      InvenduChoisie: newdata,
      discountPar: newdata[0].discount
    })
    setDealdata({
      ...dealdata,
      imageurl: newdata[0].image,
      discount:newdata[0].discount,
      PriceAfterDiscount: newdata[0].PriceAfterDiscoun,
      PriceBeforeDiscount: newdata[0].PriceBeforeDiscount,
      deal_description: newdata[0].description,
      description: e
    })
    
  }

  const getData = async () => {
    const url = `https://api.foodealzapi.com/activedeals` ;
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };
  const getInvendu = async (id) => {
    const url = `https://api.foodealzapi.com/Invendus/${id}`;
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        setData({
          ...data,
          Invendulist: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

 

  console.log('id',item)
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen1(false);
  };
  const handleClose1 = () => {
    setOpen(false);

  };

  
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const handleClick = () => {
    setOpen1(true);
  };
  const addDeal = () => {
    axios.post(`https://api.foodealzapi.com/deals/dealcreate`, {
      restaurant_id: parseInt(data.restaurant),
      imageurl: dealdata.imageurl,
      discount:dealdata.discount,
      PriceAfterDiscount: dealdata.PriceAfterDiscount,
      PriceBeforeDiscount: dealdata.PriceBeforeDiscount,
      description: dealdata.description,
      deal_description: dealdata.deal_description,
      startingdate: data.startingdate,
      expireddate: data.expireddate,
     
      
    })
      .then(() => {
        console.log("added successfully");
      })
      .catch(error => {
        console.log(" problem in posting " + error)

      });

  }
  const global = () => {
    
    addDeal();
    handleClick();
    handleClose1();


  };


  return (
<div>

      <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={() =>  setOpen(true)} > valider </Button>
      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Confirmation</DialogTitle>
        <DialogContent>
          <DialogContentText >
            Are you sure you want to add this deal !!!!!
</DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose1} color="primary">
            No
</Button>
          <Button onClick={() => global()} color="primary" >
            Yes
</Button>
        </DialogActions>
      </Dialog>
      <Snackbar open={open1} autoHideDuration={1000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          deal is successfully added!
</Alert>
      </Snackbar>
      </div>
  



  );
};
export default DialogAdd;
