import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Link } from "react-router-dom";
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Baskets from "./Menu";
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import Moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',
  
}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'



}


const Menuadd = () => {

  const history = useHistory();
  const handleHistory = () => {
    history.push("/");


  }

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    items: []

  });
  const location = useLocation();
  const [basketdata, setBasketdata] = React.useState({
    name: "",
    deal_id: "",
    imageurl: "",
    discount: "",
    PriceAfterDiscount: "",
    PriceBeforeDiscount: "",
    description: "",
    deal_description: "",
    startingdate: "",
    expirydate: "",

  });

  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      name: e
    })
  }
  //  }
  const dealidchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_id: e
    })
  }
  const imagechange = (e) => {
    setBasketdata({
      ...basketdata,
      imageurl: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const beforedischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceBeforeDiscount: e
    })
  }
  const afterdischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }


  const dealdescriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_description: e
    })
  }

  const startingdatechange = (e) => {
    setBasketdata({
      ...basketdata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setBasketdata({
      ...basketdata,
      expirydate: e
    })
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);


  };

  const onClick = () => {
    addBasket();
    handleClick();
    handleHistory();



  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  const addBasket = () => {
    axios.post('https://api.foodealzapi.com/restaurants/register', {

      name: basketdata.name,
      deal_id: basketdata.deal_id,
      imageurl: basketdata.imageurl,
      discount: basketdata.discount,
      PriceAfterDiscount: basketdata.PriceAfterDiscount,
      PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
      discount: basketdata.discount,
      description: basketdata.description,
      deal_description: basketdata.deal_description,
      startingdate: basketdata.startingdate,
      expirydate: basketdata.expirydate,


    }

    ).then(() => {
      console.log("ajouter la ligne de nom :");


    })
      .catch(error => {
        console.log(error)
      });
  }





  return (
    <div >

      <div >
        <h2 style={Title1} > Enter new Menu</h2>
      </div>

      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px' }}> restaurant name :</p>
        
        <TextField style={Field}
          label="nom" defaultValue={data.resto} onChange={e => namechange(e.target.value)} />
          
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px' }}> deal id :</p>
  
        <TextField style={Field}
          label="deal_id" defaultValue={data.donner} onChange={e => dealidchange(e.target.value)} />
       
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> Image's url :</p>
       
        <TextField style={Field}
          label="url de l'image" defaultValue={data.item1} onChange={e => imagechange(e.target.value)} />
          
      </div>
       <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> discount :</p>
        <TextField style={Field}
          label="discount" defaultValue={data.item2} onChange={e => discountchange(e.target.value)} />
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> price before :</p>
        <TextField style={Field}
          label="price before" defaultValue={data.item4} onChange={e => beforedischange(e.target.value)} />
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> price after :</p>
        <TextField style={Field}
          label="price After" defaultValue={data.item3} onChange={e => afterdischange(e.target.value)} />
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> description :</p>
        <TextField style={Field}
          label="decription" defaultValue={data.item5} onChange={e => descriptionchange(e.target.value)} />
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px'}}> deal description :</p>
        <TextField style={Field}
          label="deal decription" defaultValue={data.item6} onChange={e => dealdescriptionchange(e.target.value)} />
      </div>
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> starting date :</p>
        <TextField style={Field}
          label="starting date" defaultValue={data.item7} onChange={e => startingdatechange(e.target.value)} />
      </div>3
      <div >
        <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" , marginLeft:'100px' }}> expired date :</p>
        <TextField style={Field}
          label="expired date" defaultValue={data.item8} onChange={e => expirydatechange(e.target.value)} />

      </div>




      <div style={buttonstyle}>

        <Button variant="outline-success" onClick={() => onClick()} >Valider</Button>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            basket is successfully added!
</Alert>
        </Snackbar>





      </div>

    </div>

  )

};

export default Menuadd;
