import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Moment from 'moment';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Dialog } from '@material-ui/core';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import EditIcon from '@material-ui/icons/Edit';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';



const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}

const Field = {
  marginLeft: '400px',
  width: '200px',
  marginTop: '25px',
  padding: '15px',

}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}
const MenuUpdate = (item) => {


  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    resto: JSON.stringify(item.name),
    donner: JSON.stringify(item.deal_id),
    item1: JSON.stringify(item.imageurl),
    item2: JSON.stringify(item.discount),
    item3: JSON.stringify(item.PriceAfterDiscount),
    item4: JSON.stringify(item.PriceBeforeDiscount),
    item5: JSON.stringify(item.description),
    item6: JSON.stringify(item.deal_description),
    item7: JSON.stringify(item.startingdate),
    item8: JSON.stringify(item.expirydate),




  });
  const useStyles = makeStyles((theme) => ({
    ligne: {
      display: 'flex',
      flexDirection: 'row',
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 200,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));

  const handleClick = () => {
    setOpen(true);
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }

  console.log("les items de la page dernière est :" + data.donner);
  const [basketdata, setBasketdata] = React.useState({
    name: data.resto,
    deal_id: data.donner,
    imageurl: data.item1,
    discount: data.item2,
    PriceAfterDiscount: data.item3,
    PriceBeforeDiscount: data.item4,
    description: data.item5,
    deal_description: data.item6,
    startingdate: data.item7,
    expirydate: data.item8,


  });
  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      name: e
    })
  }
  //  }
  const dealidchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_id: e
    })
  }
  const imagechange = (e) => {
    setBasketdata({
      ...basketdata,
      imageurl: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const beforedischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceBeforeDiscount: e
    })
  }
  const afterdischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }


  const dealdescriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_description: e
    })
  }

  const startingdatechange = (e) => {
    setBasketdata({
      ...basketdata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setBasketdata({
      ...basketdata,
      expirydate: e
    })
  }

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const global = (id) => {
    handleEdit(id);
    handleClick();
    handleClose1();
  };
  const handleClose1 = () => {
    setOpen(false);

  };
  const [open, setOpen] = React.useState(false);
  const [open1, setOpen1] = React.useState(false);


  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.white,
      color: theme.palette.common.black,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);
  
  const handleEdit = (id) => {


    axios.put(`https://api.foodealzapi.com/restaurants${id}`, {
      name: basketdata.name,
      deal_id: parseInt(basketdata.deal_id),
      imageurl: basketdata.imageurl,
      discount: basketdata.discount,
      PriceAfterDiscount: parseFloat(basketdata.PriceAfterDiscount),
      PriceBeforeDiscount: parseFloat(basketdata.PriceBeforeDiscount),
      discount: basketdata.discount,
      description: basketdata.description,
      deal_description: basketdata.deal_description,
      startingdate: basketdata.startingdate,
      expirydate: basketdata.expirydate,

    })

      .then(() => {
        console.log("update item" + nom);

      })
      .catch(error => {
        console.log('updating error is :' + error);
      });
  };
  const confirmation = () => {

    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        basket is successfully added!
</Alert>
    </Snackbar>
  };
  const classes = useStyles();

  return (
    <div >
      <StyledTableCell align="right">
        <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" onClick={() => setOpen(true)}  >
          <EditIcon />

        </Button>






        <Dialog
          open={open}
          onClose={handleClose1}

        >
          <DialogTitle >Enter new Menu</DialogTitle>
          <DialogContent>
            {/* <DialogContentText>
            hello world
          </DialogContentText> */}

            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>  nom:</p>

              <TextField style={Field}
                label="nom" defaultValue={data.resto} onChange={e => nomchange(e.target.value)} variant="filled" />

            </div>
            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}> image :</p>

              <TextField style={Field}
                label="url de l'image" defaultValue={data.donner} onChange={e => imagechange(e.target.value)} variant="filled" />

            </div>
            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}> price before</p>

              <TextField style={Field}
                label="price before" defaultValue={data.item1} onChange={e => pricebeforechange(e.target.value)} variant="filled" />

            </div>
            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}> price after :</p>
              <TextField style={Field}
                label="price after" defaultValue={data.item4} onChange={e => priceafterchange(e.target.value)} variant="filled" />
            </div>
            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}> discount :</p>
              <FormControl variant="filled" className={classes.formControl}>
                <InputLabel htmlFor="filled-age-native-simple">discount</InputLabel>
                <Select
                  native
                  value={basketdata.discount}
                  onChange={discountchange}
                  inputProps={{
                    name: 'discount',
                    id: 'filled-age-native-simple',
                  }}
                >
                  <option aria-label="None" value="" />
                  <option value={10}>10%</option>
                  <option value={20}>20%</option>
                  <option value={30}>30%</option>
                  <option value={40}>40%</option>
                  <option value={50}>50%</option>
                  <option value={60}>60%</option>
                  <option value={70}>70%</option>
                  <option value={80}>80%</option>
                  <option value={90}>90%</option>
                </Select>
              </FormControl>
            </div>

            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}> description :</p>
              <TextField style={Field}
                label="description" defaultValue={data.item3} onChange={e => descriptionchange(e.target.value)} variant="filled" />
            </div>
            <div className={classes.ligne}>
              <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}> id restaurant :</p>
              <TextField style={Field}
                label="id restaurant" defaultValue={data.item5} onChange={e => idrestaurantchange(e.target.value)} variant="filled" />
            </div>




          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose1} style={{ backgroundColor: '#008037', borderRadius: '5px' }}>
              No
</Button>
            <Button style={{ backgroundColor: '#008037', borderRadius: '5px' }} variant="outline-success" onClick={() => global(data.donner)}  >confirmer</Button>


          </DialogActions>
        </Dialog>


        {confirmation()}

      </StyledTableCell>





    </div>

  )

};


export default MenuUpdate;
