import React, { useState, useEffect } from "react";


import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import Paper from '@material-ui/core/Paper';
import TableRow from '@material-ui/core/TableRow';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
// import Usersadd from './Usersadd';
// import UsersUpdate from './UsersUpdate';

import axios from 'axios';
import { Dialog } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

const Users = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const [userdata, setUserdata] = React.useState({
    firstname: "",
    lastname: "",
    mail: "",
    phone: "",
    age: "",
    city: "",
    password: ""
  });
  const [q, setQ] = useState("");

  const [visible, setVisible] = React.useState(false);
  const [visible1, setVisible1] = React.useState(false);

  const showDialog = () => {
    setVisible(true);
  };
  // const showDialog1 = () => {
  //   setVisible(true);
  // };
  const handleCancel = () => {
    setVisible1(false);
  };
 


  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  
const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);
const useStyles = makeStyles({
  table: {
    minWidth: 400,
  },
});

  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/users/${id}`)
      .then(res => {
       getData();
      })
      .catch((error) => {
        console.log(error);
      })
  }

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/users';
    let user;
    user = [];
    await fetch(url)
      .then(res => res.json())
      .then(res => {

        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,
          

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  useEffect(() => {
    getData()
  }, [])

  const searchFilterFunction = (text) => {
    const newData = data.temp.filter((item) => {
      const itemData = `${item.firstname.toUpperCase()}`;
      const textData = text.toUpperCase();
      return itemData.indexOf(textData) > -1;
    });
    setData({
      ...data,
      data: newData
    })
  };
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: '#b4b4b4b4b4',
    marginLeft: '20px',
    marginTop: '20px',

    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'

  }
  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '100px',
    borderRadius: '5px'
  }
  const Field = {
    marginLeft: '20px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


  }

  const classes = useStyles();

  return (
    <div>
      <div style={Title1}>
        <h2>Users</h2>

      </div>

      {/* <div style={buttonstyle}>
        <Link to='/Usersadd'>

          <Button variant="outline-success" >add user </Button>
        </Link>



      </div> */}

      <div style={Search1} >
        <input type="text" placeholder=" Search Here..."
          contentEditable={true}
          onChange={e => searchFilterFunction(e.target.value)} />
      </div>
      <div style={item} >
        <Paper >
          <TableContainer >
            <Table className={classes.table}>
              <TableHead>
                <TableRow>
                  <StyledTableCell style={{ alignItems: 'center' }} >userId</StyledTableCell>
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userFirstname</StyledTableCell>
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userLastname</StyledTableCell>
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userMail</StyledTableCell>
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userPhone</StyledTableCell>
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userAge</StyledTableCell>
                  {/* <CustomTableCell style={{ alignItems: 'center' }} align="right">userPassword</CustomTableCell> */}
                  <StyledTableCell style={{ alignItems: 'center' }} align="right">userCity</StyledTableCell>
                  {/* <CustomTableCell style={{ alignItems: 'center' }} align="right"></CustomTableCell> */}
                  <StyledTableCell style={{ alignItems: 'center' }} align="right"></StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {data.data.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((item, user_id) => {
                  return (
                    <StyledTableRow hover role="checkbox" tabIndex={-1} key={user_id}>

                      <StyledTableCell>

                        {item.user_id}
                      </StyledTableCell>
                      <StyledTableCell align="right">
                        {item.firstname}
                      </StyledTableCell>
                      <StyledTableCell align="right">
                        {item.lastname}
                      </StyledTableCell>
                      <StyledTableCell align="right">
                        {item.mail}
                      </StyledTableCell>
                      <StyledTableCell align="right">
                        {item.phone}
                      </StyledTableCell>
                      <StyledTableCell align="right">
                        {item.age}
                      </StyledTableCell>
                      {/* <CustomTableCell align="right">
                  {item.password}
                </CustomTableCell> */}
                      <StyledTableCell align="right">
                        {item.ville}
                      </StyledTableCell>
                      {/* <CustomTableCell align="right">

                        <Link to='/UsersUpdate'>
                          <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" >
                            <EditIcon />

                          </Button>
                        </Link>
                      </CustomTableCell> */}
                      <StyledTableCell align="right">
                        <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" >
                          <ClearIcon />

                        </Button>
                      </StyledTableCell>
                      {/* <Dialog

                      >
                        <DialogTitle >Confirmation</DialogTitle>
                        <DialogContent>
                          <DialogContentText >
                            you are sure !! you want to delete this User?
</DialogContentText>
                        </DialogContent>
                        <DialogActions>
                          <Button onClick={handleCancel} color="primary">
                            cancel
</Button>
                          <Button  color="primary" >
                            Agree
</Button>
                        </DialogActions>
                      </Dialog> */}
                    </StyledTableRow>
                  );
                })};


          </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[10, 25, 100]}
            component="div"
            count={data.data.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onChangePage={handleChangePage}
            onChangeRowsPerPage={handleChangeRowsPerPage}
          />
        </Paper>
      </div>
      <div>

      </div>

    </div>
  )
}
export default Users;
