import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import DialogActions from '@material-ui/core/DialogActions';
import MuiAlert from '@material-ui/lab/Alert';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation ,BrowserRouter as Router , useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';





const Title1 = {
  fontSize: '50px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  marginTop:'20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  flex_direction:'row',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '150px',
  borderRadius: '5px'

}


const Partnersadd = () => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    partnerItems: []

  });

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () =>{
    history.push("/");
  }

  const location = useLocation();
  const [partnerdata, setPartnerdata] = React.useState({
    partnerName: "",
    partnerMail: "",
    partnerPassword:"",
    typePayment: "",
    description: "",
    address: "",
    url: "",
    phone: "",
    latitude: "",
    longitude: "",
    type: "",
    commission_rate: "",
    logourl: "",
    image: "",
    discount: ""


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
    })
  }

  const mailchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerMail: e
    })
  }
  const passwordchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerPassword: e
    })
  }
  const typePaymentchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      typePayment: e
    })
  }
  const descriptionchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      description: e
    })
  }
  const addresschange = (e) => {
    setPartnerdata({
      ...partnerdata,
      address: e
    })
  }
  const urlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      url: e
    })
  }
  const phonechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      phone: e
    })
  }
  const latitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      latitude: e
    })
  }
  const longitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      longitude: e
    })
  }
  const typechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      type: e
    })
  }
  const commission_ratechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      commission_rate: e
    })
  }
  const logourlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      logourl: e
    })
  }
  const imagechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      image: e
    })
  }
  const discountchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      discount: e
    })
  }


  const addPartner = () => {
    console.log(partnerdata.partnerPassword)
    axios.post('https://api.foodealzapi.com/restaurants/register', {
      name: partnerdata.partnerName,
      mail: partnerdata.partnerMail,
      password: partnerdata.partnerPassword,
      typePayment: partnerdata.typePayment,
      description: partnerdata.description,
      address: partnerdata.address,
      url: partnerdata.url,
      phone: parseInt(partnerdata.phone),
      // latitude: partnerdata.latitude,
      // longitude: partnerdata.longitude,
      type: partnerdata.type,
      commission_rate: parseInt(partnerdata.commission_rate),
      logourl: partnerdata.logourl,
      image: partnerdata.image,
      discount: partnerdata.discount
    })
  
      .then(() => {

        console.log("done")
      })
      .catch(error => {
        console.log(" problem in posting "+error)

      });

  }
  // const onClick = () => {
  //   addPartner();
  //   handleClick();
  //   handleHistory();
    

  // };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };


  return (
    <div style={{backgroundColor: "#FF9F54"}} >
      <div>
        <ArrowBackIosIcon style={{color: "#008037" , fontSize:40 , marginTop: "20px", marginLeft:"20px"}} onClick={() => { handleHistory()} }/>
      </div>
      <div style={{marginLeft:"30px", flexDirection:"row"}}>
      <img src={`${process.env.PUBLIC_URL}/static/images/foodealz1.png`}  />
      </div>
      <h2 style={Title1} >Enter new Partner</h2>

      <div style={Field}>
       <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>name :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="name" onChange={e => namechange(e.target.value)}  variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px' , color:"#008037", fontWeight: "bold"}}>mail :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="mail" onChange={e => mailchange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px' , color:"#008037", fontWeight: "bold"}}>password :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="password" onChange={e => passwordchange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px' , color:"#008037", fontWeight: "bold"}}>type de paiement :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="type de paiement" onChange={e => typePaymentchange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px' , color:"#008037", fontWeight: "bold"}}>description :</p>
        <TextField style={{ marginLeft: '100px'  }}
          label="description" onChange={e => descriptionchange(e.target.value)} variant="filled"/>
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>address :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="address" onChange={e => addresschange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px' , color:"#008037", fontWeight: "bold"}}>site web :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="url de site web" onChange={e => urlchange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>phone :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="phone" onChange={e => phonechange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>latitude :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="latitude" onChange={e => latitudechange(e.target.value)} variant="filled" />
          </div>
          
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>longitude :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="longitude" onChange={e => longitudechange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>type :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="type" onChange={e => typechange(e.target.value)} variant="filled"/>
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>commission rate :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="commission_rate" onChange={e => commission_ratechange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>logo :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="logo url" onChange={e => logourlchange(e.target.value)} variant="filled" />
          </div>

          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>image :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="image" onChange={e => imagechange(e.target.value)} variant="filled" />
          </div>
          <div style={Field}>
          <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold"}}>discount :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="discount" onChange={e => discountchange(e.target.value)} variant="filled" />
          </div>
          

      




      <Button style={buttonstyle} onClick={() => addPartner()} > valider </Button>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
            <Alert onClose={handleClose} severity="success">
              partner is successfully added!
</Alert>
          </Snackbar>



    </div>

  )

};

export default Partnersadd;
