import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import Paper from '@material-ui/core/Paper';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";

import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import axios from 'axios';
import Offressadd from './Offresadd';
import OffresUpdate from './OffresUpdate';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';


const Offres = () => {

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [data, setData] = React.useState({

    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,
    array: [],


  });
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const [q, setQ] = useState("");
  const [visible, setVisible] = React.useState(false);
  const [visible1, setVisible1] = React.useState(false);

  // const showDialog = () => {
  //   setVisible(true);
  // };
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose1 = () => {
    setOpen(false);
  };
  const [open1, setOpen1] = React.useState(false);
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen1(true);
  };

  const onClick = (id) => {
    handleClick();
    handleRemove(id);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen1(false);
  };

  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.white,
      color: theme.palette.common.black,
     
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  
const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);
const useStyles = makeStyles({
  table: {
    minWidth: 400,
    marginTop: 20 ,
  },
});

  useEffect(() => {
    getData()
  }, [])

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/deals';
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };



  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/deals/${id}`)
      .then(res => {
        getData();

        // axios.get('https://api.foodealzapi.com/restaurants')
        // setData({
        //   ...data,
        //   data: res,
        //   error: 'Error deleting content',
        // })
        // res.header('Access-Control-Allow-Origin', '*'),

        //   res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");




      })
      .catch((error) => {
        console.log(error);
      })
  };
  const searchFilterFunction = (id) => {
    const newData = data.temp.filter((item) => {
      const itemData = `${item.id}`;
      const textData = id;
      return itemData.indexOf(textData) > -1;
    });
    setData({
      ...data,
      data: newData
    })
  };
