import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';




const Title1 = {
  fontSize: '50px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  marginTop: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  flexDirection:'row',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '150px',
  borderRadius: '5px'

}


const Offresadd = () => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,


  });

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }

  const location = useLocation();
  const [offredata, setOffredata] = React.useState({
    id: "",
    quantity: "",
    nbre_redeemed_deal: "",
    startingdate: "",
    expirydate: "",
    startingdate_hours: "",
    expirydate_hours: "",
    active: "",
    restaurant_id: "",
    deal_id: ""



  });

  const idchange = (e) => {
    setOffredata({
      ...offredata,
      id: e
    })
  }

  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const startingdatechange = (e) => {
    setOffredata({
      ...offredata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setOffredata({
      ...offredata,
      expirydate: e
    })
  }
  const startingdate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      startingdate_hours: e
    })
  }
  const expirydate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      expirydate_hours: e
    })
  }
  const activechange = (e) => {
    setOffredata({
      ...offredata,
      active: e
    })
  }

  const restaurant_idchange = (e) => {
    setOffredata({
      ...offredata,
      restaurant_id: e
    })
  }
  const deal_idchange = (e) => {
    setOffredata({
      ...offredata,
      deal_id: e
    })
  }
  //   const typechange = (e) => {
  //     setPartnerdata({
  //       ...partnerdata,
  //       type: e
  //     })
  //   }
  //   const commission_ratechange = (e) => {
  //     setPartnerdata({
  //       ...partnerdata,
  //       commission_rate: e
  //     })
  //   }
  //   const logourlchange = (e) => {
  //     setPartnerdata({
  //       ...partnerdata,
  //       logourl: e
  //     })
  //   }
  //   const imagechange = (e) => {
  //     setPartnerdata({
  //       ...partnerdata,
  //       image: e
  //     })
  //   }
  //   const discountchange = (e) => {
  //     setPartnerdata({
  //       ...partnerdata,
  //       discount: e
  //     })
  //   }


  const addOffre = () => {

    axios.post('https://api.foodealzapi.com/deals/dealcreate', {
      id: offredata.id,
      quantity: offredata.quantity,
      nbre_redeemed_deal: offredata.nbre_redeemed_deal,
      startingdate: offredata.startingdate,
      expirydate: offredata.expirydate,
      startingdate_hours: offredata.startingdate_hours,
      expirydate_hours: offredata.expirydate_hours,
      active: offredata.active,
      restaurant_id: offredata.restaurant_id,
      deal_id: offredata.deal_id,
      //   logourl: partnerdata.logourl,
      //   image: partnerdata.image,
      //   discount: partnerdata.discount
    })
      .then(() => {

        console.log("done")
      })
      .catch(error => {
        console.log(error)

      });

  }
  const onClick = () => {
    addOffre().then ((res ) =>console.log(" offer sucessfully added")).catch ((error) => console.log("ilya problème dans l'ajout"));
    handleClick();
    handleHistory();

    


  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };


  return (
    <div style={{ backgroundColor: "#FF9F54" }}>
      <div>
        <ArrowBackIosIcon style={{ color: "#008037", fontSize: 40, marginTop: "20px", marginLeft: "20px" }} onClick={() => { handleHistory() }} />
      </div>
      <h2 style={Title1} >Enter new deal</h2>

      <div style={Field}>
        <p style={{fontSize: '20px'}}>id :</p>
        <TextField  style={{ marginLeft: '100px' }}
          label="id" onChange={e => idchange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
        <p style={{fontSize: '20px'}}>quantity :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="quantity" onChange={e => qtchange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>quantity sold :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="quantity sold" onChange={e => offresoldchange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>starting date :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="starting date" onChange={e => startingdatechange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>expired date :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="expiry date" onChange={e => expirydatechange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>starting hour :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="starting hour" onChange={e => startingdate_hourschange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>expired hour :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="expiry date" onChange={e => expirydate_hourschange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p> style={{fontSize: '20px'}}active or not :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="active or not" onChange={e => activechange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>restaurant id :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="restaurant id" onChange={e => restaurant_idchange(e.target.value)} variant="filled" />
      </div>
      <div style={Field}>
      <p style={{fontSize: '20px'}}>deal id :</p>
        <TextField style={{ marginLeft: '100px' }}
          label="deal id" onChange={e => deal_idchange(e.target.value)} variant="filled" />
        {/* <TextField style={{ marginLeft: '150px' }}
          label="type" onChange={e => typechange(e.target.value)} />
        <TextField style={{ marginLeft: '20px' }}
          label="commission_rate" onChange={e => commission_ratechange(e.target.value)} />
        <TextField style={{ marginLeft: '100px' }}
          label="logo url" onChange={e => logourlchange(e.target.value)} />
          </div>
