import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { useLocation ,BrowserRouter as Router , useHistory } from "react-router-dom";




const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '80px',
  borderRadius: '5px'



}
const MenuUpdate = () => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,


  });
 

  const handleClick = () => {
    setOpen(true);
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const history = useHistory();
  const handleHistory = () =>{
    history.push("/");
  }

  const location = useLocation();
  const [basketdata, setBasketdata] = React.useState({
    nom: location.state.detail.item.nom,
    PriceBeforeDiscount: location.state.detail.item.PriceBeforeDiscount,
    PriceAfterDiscoun: location.state.detail.item.PriceAfterDiscoun,
    discount: location.state.detail.item.Discount,
    description: location.state.detail.item.Description,
    restaurant_id: location.state.detail.item.restaurant_id
  });

  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      nom: e
    })
  }
  const beforedischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceBeforeDiscount: e
    })
  }
  const afterdischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscoun: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }
  const restidchange = (e) => {
    setBasketdata({
      ...basketdata,
      restaurant_id: e
    })
  }
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const onClick = (id) => {
    handleEdit(id);
    handleClick();
    

  };
  const [open, setOpen] = React.useState(false);



  const handleEdit = (id) => {

    axios.put(`http://192.168.1.3:5000/api/entreroffres/${id}`, {
      nom: basketdata.nom,
      PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
      PriceAfterDiscoun: basketdata.PriceAfterDiscoun,
      discount: basketdata.discount,
      description: basketdata.description,
      restaurant_id: basketdata.restaurant_id
        
  })

    .then(() => {
    console.log("done");
     
    })
  .catch(error => {
    console.log(error);
  });
  };
  console.log(location.state)

return (
  <div >
     <div>
        <ArrowBackIosIcon style={{color: "#008037" , fontSize:40 , marginTop: "20px", marginLeft:"20px"}} onClick={() => { handleHistory()} }/>
      </div>
    <div style={Title1}>
      <h2> Update Menu</h2>
    </div>
    <div style={Field}>
      <TextField
        required id="standard-required" label="name" defaultValue={basketdata.nom} onChange={e=>namechange(e.target.value)} />

      <TextField style={{ marginLeft: '50px' }}
        required id="standard-required" label="origin price" defaultValue={basketdata.PriceBeforeDiscount} onChange={e=>beforedischange(e.target.value)} />

    </div >

    <div style={Field}>
      <TextField
        required id="standard-required" label="price after reduction" defaultValue={basketdata.PriceAfterDiscoun} onChange={e=>afterdischange(e.target.value) } />
    </div>
    <div style={Field}>
      <TextField
        required id="standard-required" label="discount" defaultValue={basketdata.Discount} onChange={e=>discountchange(e.target.value) }  />

      <TextField style={{ marginLeft: '50px' }}
        required id="standard-required" label="description" defaultValue={basketdata.Description} onChange={e=>descriptionchange(e.target.value) } />

      <TextField style={{ marginLeft: '50px' }}
        required id="standard-required" label="restaurant_id" defaultValue={basketdata.restaurant_id} onChange={e=>restidchange(e.target.value) } />
    </div>
    <div style={buttonstyle}>
      <Button variant="outline-success" onClick={() =>onClick(location.state.detail.item.id)}  >confirmer</Button>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
            <Alert onClose={handleClose} severity="success">
              partner is successfully added!
</Alert>
          </Snackbar>
    </div>

  </div>

)

};


export default MenuUpdate;
