import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import Paper from '@material-ui/core/Paper';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";

import { withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import axios from 'axios';
import Partnersadd from './Partnersadd';
import PartnersUpdate from './PartnersUpdate';


const Partners = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };
  const [q, setQ] = useState("");

  // const showDialog = () => {
  //   setVisible(true);
  // };

  const CustomTableCell = withStyles(theme => ({
    body: {
      borderBottomColor: 'transparent',
      paddingTop: theme.spacing(1),
      paddingBottom: theme.spacing(1)
    }
  }))(TableCell);

  useEffect(() => {
    setTimeout(async () => {

      getData()

      // let restaurantid;
      // restaurantid = null;
      // try {
      //   restaurantid = await AsyncStorage.getItem('restaurant_id');
      // } catch (e) {
      //   console.log(e);
      // }

      // console.log('focused section');
    }



      , 500);


  }, [])

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/restaurants';
    let user;
    user = [];
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };



  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/restaurants/${id}`)
      .then(res => {
        res.header('Access-Control-Allow-Origin', '*'),

          res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
        console.log(id)
        if (res.data == 'partner Deleted!') {
          // handleCancel()
          getData()

        }
      })
      .catch((error) => {
        console.log(error);
      })
  }
  // const searchFilterFunction = (text) => {
  //   const newData = data.temp.filter((item) => {
  //     const itemData = `${item.nom.toUpperCase()}`;
  //     const textData = text.toUpperCase();
  //     return itemData.indexOf(textData) > -1;
  //   });
  //   setData({
  //     ...data,
  //     data: newData
  //   })
  // };
  // const addPartner = () => {
  //   let partnerItems = localStorage.getItem('partneritem');

  //   console.log(partnerItems)
  //   axios.post('https://api.foodealzapi.com/restaurants', partnerItems).then((res) => {

  //     console.log(res.data)
  //   })
  //     .catch(error => {
  //       console.log(error)
  //     });
  //   // partnerItems.map((item) => {

  //   // })


  //   setData({
  //     ...data,

  //   })
  //   // navigation.jumpTo('Mes Couffins')
  //   // navigation.goBack()
  // }


  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: 'white',
    marginLeft: '20px',
    marginTop: '20px',
    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'
  }

  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '110px',
    borderRadius: '5px'
  }


  return (
    <div>
      <div style={Title1}>
        <h2>Partners</h2>

        <div style={buttonstyle}>
          <Link to='/Partnersadd'>
            <Button variant="outline-success"  >add partner </Button>
          </Link>


        </div>

      </div>
      <div style={Search1} >
        <input type="text" placeholder=" Search Here..." value={q} onChange={(e) => setQ(e.target.value)} />
      </div>
      <div style={item}>
        <Paper >
          <TableContainer >
            <Table>
              <TableHead>
                <TableRow>
                  <CustomTableCell style={{ alignItems: 'center' }} >partnerId</CustomTableCell>
                  <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerName</CustomTableCell>
                  <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerMail</CustomTableCell>
                  <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerPhone</CustomTableCell>

                  {/* <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerPassword</CustomTableCell> */}
                  <CustomTableCell style={{ alignItems: 'center' }} align="right">partnerCity</CustomTableCell>
                  <CustomTableCell style={{ alignItems: 'center' }} align="right"> </CustomTableCell>
                  <CustomTableCell style={{ alignItems: 'center' }} align="right"> </CustomTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {data.data.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((item, restaurant_id) => {
                  return (
                    <TableRow hover role="checkbox" tabIndex={-1} key={restaurant_id}>
                      <CustomTableCell>

                        {item.restaurant_id}
                      </CustomTableCell>
                      <CustomTableCell align="right">
                        {item.name}
                      </CustomTableCell>
                      <CustomTableCell align="right">
                        {item.mail}
                      </CustomTableCell>
                      <CustomTableCell align="right">
                        {item.phone}
                      </CustomTableCell>
                      {/* <CustomTableCell align="right">
                  {item.password}
                </CustomTableCell> */}
                      <CustomTableCell align="right">
                        {item.address}
                      </CustomTableCell>
                      <CustomTableCell align="right">

                        <Link to='/PartnersUpdate' >
                          <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" >
                            <EditIcon />

                          </Button>
                        </Link>
                      </CustomTableCell>
                      <CustomTableCell align="right">
                        <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" onClick={() => this.handleRemove(restaurant_id)}>
                          <ClearIcon />
                        </Button>
                      </CustomTableCell>

                    </TableRow>
                  );
                })};
          </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
            rowsPerPageOptions={[10, 25, 100]}
            component="div"
            count={data.data.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onChangePage={handleChangePage}
            onChangeRowsPerPage={handleChangeRowsPerPage}
          />
        </Paper>


      </div>

    </div>

  )

}
export default Partners;
