import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Link } from "react-router-dom";
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Baskets from "./Menu";
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import Moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';




const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '80px',
  borderRadius: '5px'



}


const Menuadd = () => {

  const history = useHistory();
  const handleHistory = () => {
    history.push("/");


  }

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    items: []

  });
  const location = useLocation();
  const [basketdata, setBasketdata] = React.useState({
    name: "",
    deal_id: "",
    imageurl:"",
    discount:"",
    PriceAfterDiscount: "",
    PriceBeforeDiscount: "",
    description:"",
    deal_description:"",
    startingdate:"",
    expirydate:"",

  });

  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      name: e
    })
  }
  //  }
  const dealidchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_id: e
    })
  }
  const imagechange = (e) => {
    setBasketdata({
      ...basketdata,
      imageurl: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const beforedischange = (e) => {
      setBasketdata({
        ...basketdata,
        PriceBeforeDiscount: e
      })
    }
     const afterdischange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }
  
 
  const dealdescriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      deal_description: e
    })
  }

  const startingdatechange = (e) => {
    setBasketdata({
      ...basketdata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setBasketdata({
      ...basketdata,
      expirydate: e
    })
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  

  };

  const onClick = () => {
    addBasket();
    handleClick();
    
   

  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  const addBasket = () => {
    axios.post('http://192.168.22.78:5000/api/entreroffres/register', {

      name: basketdata.name,
      deal_id: basketdata.deal_id,
      imageurl: basketdata.imageurl,
      discount: basketdata.discount,
      PriceAfterDiscount: basketdata.PriceAfterDiscount,
      PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
      discount: basketdata.discount,
      description: basketdata.description,
      deal_description: basketdata.deal_description,
      startingdate: basketdata.startingdate,
      expirydate: basketdata.expirydate,

      
    }

    ).then(() => {
      console.log("done");


    })
      .catch(error => {
        console.log(error)
      });
  }





  return (
    <div >
      
      <div style={Title1}>
        <h2 > Enter new Menu</h2>
      </div>
      <div style={Field}>
        <TextField
          label="nom" onChange={e => namechange(e.target.value)} />
        <TextField
          label="deal_id" onChange={e => dealidchange(e.target.value)} />
        <TextField
          label="url de l'image" onChange={e => imagechange (e.target.value)} />
        <TextField
          label="discount" onChange={e => discountchange(e.target.value)} />
           <TextField
          label="price before" onChange={e => beforedischange(e.target.value)} />
            <TextField
          label="price After" onChange={e => afterdischange(e.target.value)} />
           <TextField
          label="decription" onChange={e => descriptionchange(e.target.value)} />

<TextField
          label="deal decription" onChange={e => dealdescriptionchange(e.target.value)} />
          <TextField
          label="starting date" onChange={e => startingdatechange(e.target.value)} />    
          <TextField
          label="expired date" onChange={e => expirydatechange(e.target.value)} />  
           </div>
     


      <div style={buttonstyle}>

        <Button variant="outline-success" onClick={() => onClick() } >Valider</Button>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        basket is successfully added!
</Alert>
    </Snackbar>
    




      </div>

    </div>

  )

};

export default Menuadd;
