import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { withStyles, makeStyles } from '@material-ui/core/styles';


const AddAdmin = () => {

    // const { register, handleSubmit, error } = useForm({
    //     resolver: yupResolver(schema),
    // });

    const [data, setData] = React.useState({
    
        error: '',
        data: [],
        temp: [],
        error: null,
        username:'',
        email:'',
        password:''
       
      });

      const addAdmin = () => {
        axios.post(`https://api.foodealzapi.com/Admin/register`, {

        username:data.username,
        email:data.email,
        password:data.password,
        })
          .then((res) => {
            console.log("add done");
          })
          .catch(error => {
            console.log(" problem in posting " + error)
    
          });
    
      }

      const usernamechange = (e) => {
        setData({
          ...data,
          username: e
        })
      }

      const emailchange = (e) => {
        setData({
          ...data,
          email: e
        })
      }

      const passwordchange = (e) => {
        setData({
          ...data,
          password: e
        })
      }

    // const submitForm = () => {
    //     console.log(data.username);
    //     console.log(data.email);
    //     console.log(data.password);
    // };
    

    return (
        <div style={{marginLeft:"400px"}}>

            <h2>Add an Admin</h2>
            <form onSubmit={() =>{submitForm()}}>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>username:</p>
                    <input type="text" name="username" onChange={e => usernamechange(e.target.value)} placeholder="enter username"  />
                 
                    
                </div>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>email :</p>
                    <input type="text" name="email" placeholder="enter email" onChange={e => emailchange(e.target.value)}   />
             
                    
                </div>
                <div >
                    <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>password :</p>
                    <input style={{width:"150px"}} type="text" name="password" placeholder="enter password" onChange={e => passwordchange(e.target.value)}  />
                   
                  
                </div>

              

            </form>
            <Button style={{marginTop:"50px", marginLeft:"50px", backgroundColor:"#008037", width:"100px"}} onClick={() => {addAdmin()}}> submit</Button>
        </div>

    );
}
export default AddAdmin;
