import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { useLocation ,BrowserRouter as Router , useHistory } from "react-router-dom";




const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '80px',
  borderRadius: '5px'



}
const MenuUpdate = () => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,


  });
 

  const handleClick = () => {
    setOpen(true);
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const history = useHistory();
  const handleHistory = () =>{
    history.push("/");
  }

  const location = useLocation();
  const [basketdata, setBasketdata] = React.useState({
    nom: "",
    partner_id: "",
    createdAt: Moment("").format('DD-MM-YYYY'),
    updatedAt: Moment("").format('DD-MM-YYYY'),
    // nom: location.state.detail.item.nom,
    // PriceBeforeDiscount: location.state.detail.item.PriceBeforeDiscount,
