    // description: location.state.detail.item.Description,
    // restaurant_id: location.state.detail.item.restaurant_id
  });
  const namechange = (e) => {
    setBasketdata({
      ...basketdata,
      nom: e
    })
  }
  //  }
  const partneridchange = (e) => {
    setBasketdata({
      ...basketdata,
      partner_id: e
    })
  }
  const createchange = (e) => {
    setBasketdata({
      ...basketdata,
      createdAt: e
    })
  }
  const updatechange = (e) => {
    setBasketdata({
      ...basketdata,
      updatedAt: e
    })
  }
  // const namechange = (e) => {
