  //     discount: e
  //   })
  // }
  // const descriptionchange = (e) => {
  //   setBasketdata({
  //     ...basketdata,
  //     description: e
  //   })
  // }
  // const restidchange = (e) => {
  //   setBasketdata({
  //     ...basketdata,
  //     restaurant_id: e
  //   })
  // }
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const global = (id) => {
    console.log(id);
    handleEdit(id);
    handleClick();
    

  };
  const [open, setOpen] = React.useState(false);



  const handleEdit = (id) => {
    
    console.log(id);

    axios.put(`http://192.168.22.46:5000/api/entreroffres/${id}`, {
      nom: basketdata.nom,
      partner_id: basketdata.partner_id,
      createdAt: basketdata.createdAt,
      updatedAt: basketdata.updatedAt,
      // nom: basketdata.nom,
      // PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
      // PriceAfterDiscoun: basketdata.PriceAfterDiscoun,
      // discount: basketdata.discount,
      // description: basketdata.description,
      // restaurant_id: basketdata.restaurant_id
        
  })

    .then(() => {
    console.log("update item");
     
    })
  .catch(error => {
    console.log( 'updating error is :'+error);
  });
  };
  console.log("location items is :" + location.state.detail.item);

return (
  <div >
     <div>
        <ArrowBackIosIcon style={{color: "#008037" , fontSize:40 , marginTop: "20px", marginLeft:"20px"}} onClick={() => { handleHistory()} }/>
      </div>
    <div style={Title1}>
      <h2> Update Menu</h2>
    </div>
    <div style={Field}>
        <TextField
          label="nom" onChange={e => namechange(e.target.value)} />
        <TextField
          label="partner_id" onChange={e => partneridchange(e.target.value)} />
        <TextField
          label="createdAt" onChange={e => createchange(e.target.value)} />
        <TextField
          label="UpdatedAt" onChange={e => updatechange(e.target.value)} />
      </div>
    {/* <div style={Field}>
      <TextField
        required id="standard-required" label="name" defaultValue={basketdata.nom} onChange={e=>namechange(e.target.value)} />

      <TextField style={{ marginLeft: '50px' }}
