    </div>
    <div style={Field}>
      <TextField
        required id="standard-required" label="discount" defaultValue={basketdata.Discount} onChange={e=>discountchange(e.target.value) }  />

      <TextField style={{ marginLeft: '50px' }}
        required id="standard-required" label="description" defaultValue={basketdata.Description} onChange={e=>descriptionchange(e.target.value) } />

      <TextField style={{ marginLeft: '50px' }}
        required id="standard-required" label="restaurant_id" defaultValue={basketdata.restaurant_id} onChange={e=>restidchange(e.target.value) } />
    </div> */}
    <div style={buttonstyle}>
      <Button variant="outline-success" onClick={() =>global(location.state.detail.item.id)}  >confirmer</Button>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
            <Alert onClose={handleClose} severity="success">
              partner is successfully updated!
</Alert>
          </Snackbar>
    </div>

  </div>

)

};


export default MenuUpdate;
