import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import axios from 'axios';
// import Basketsadd from './Basketsadd';
// import BasketsUpdate from './BasketsUpdate';
import { Dialog } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';


const Baskets = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [q, setQ] = useState("");
  const [visible, setVisible] = React.useState(false);
  const [basketdata, setBasketdata] = React.useState({
    name: "",
    PriceBeforeDis: "",
    PriceAfterDiscount: "",
    Discount: "",
    Description: "",
    restaurant_id: ""
  });

  const showDialog = () => {
    setVisible(true);
  };
  const showDialog1 = () => {
    setVisible(true);
  };
  const handleCancel = () => {
    setVisible(false);
  };
  const handleEdit = () => {
   
    axios.put('https://api.foodealzapi.com/Invendus/4', basketdata)

      .then((basketdata) => {
        console.log(basketdata);
      })
      .catch(error => {
        console.log(error);
      });
  }

  const CustomTableCell = withStyles(theme => ({
    body: {
      borderBottomColor: 'transparent',
      paddingTop: theme.spacing(1),
      paddingBottom: theme.spacing(1)
    }
  }))(TableCell);

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/Invendus/4';
    let user;
    user = [];
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  useEffect(() => {
    setTimeout(async () => {

      getData()

      // let userid;
      // userid = null;
      // try {
      //   userid = await AsyncStorage.getItem('user_id');
      // } catch (e) {
      //   console.log(e);
      // }

      // console.log('focused section');
    }



      , 500);


  }, [])

  // const searchFilterFunction = (text) => {
  //   const newData = data.temp.filter((item) => {
  //     const itemData = `${item.nom.toUpperCase()}`;
  //     const textData = text.toUpperCase();
  //     return itemData.indexOf(textData) > -1;
  //   });
  //   setData({
  //     ...data,
  //     data: newData
  //   })
  // };
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: 'white',
    marginLeft: '20px',
    marginTop: '20px',
    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'
  }
  const Field = {
    marginLeft: '20px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


  }
  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '100px',
    borderRadius: '5px'
  }
  const addBasket = () => {
    let items = localStorage.getItem('itemInvendu');

    console.log(items)
    axios.post('https://api.foodealzapi.com/Invendus/4', items).then((res) => {

      console.log(res.data)
    })
      .catch(error => {
        console.log(error)
      });
    // items.map((item) => {

    // })
    // sessioncStorage.setItem('items', JSON.stringify([]));

    setData({
