import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'



}
const PartnersUpdate = (itemupdate, props) => {
  const location = useLocation();
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    donner: JSON.stringify(location.state.ligne.item.restaurant_id),
    item1: JSON.stringify(location.state.ligne.item.name),
    item2: JSON.stringify(location.state.ligne.item.mail),
    item3: JSON.stringify(location.state.ligne.item.password),
    item4: JSON.stringify(location.state.ligne.item.typePayment),
    item5: JSON.stringify(location.state.ligne.item.description),
    item6: JSON.stringify(location.state.ligne.item.address),
    item7: JSON.stringify(location.state.ligne.item.url),
    item8: JSON.stringify(location.state.ligne.item.phone),
    item9: JSON.stringify(location.state.ligne.item.latitude),
    item10: JSON.stringify(location.state.ligne.item.longitude),
    item11: JSON.stringify(location.state.ligne.item.type),
    item12: JSON.stringify(location.state.ligne.item.commission_rate),
    item13: JSON.stringify(location.state.ligne.item.logourl),
    item14: JSON.stringify(location.state.ligne.item.image),
    item15: JSON.stringify(location.state.ligne.item.discount)
  });


  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }
  console.log("les items de la page derniÃ¨re est :" + data.donner);
  const [partnerdata, setPartnerdata] = React.useState({
    partnerName: data.item1,
    partnerMail: data.item2,
    description: data.item5,
    address: data.item6,
    url: data.item7,
    phone: data.item8,
    latitude: data.item9,
    longitude: data.item10,
    type: data.item11,
    commission_rate: data.item12,
    logourl: data.item13,
    image: data.item14,
    discount: data.item15


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
    })
  }

  const mailchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerMail: e
    })
  }

  const descriptionchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      description: e
    })
  }
  const addresschange = (e) => {
    setPartnerdata({
      ...partnerdata,
      address: e
    })
  }
  const urlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      url: e
    })
  }
  const phonechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      phone: e
    })
  }
  const latitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      latitude: e
    })
  }
  const longitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      longitude: e
    })
  }
  const typechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      type: e
    })
  }
  const commission_ratechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      commission_rate: e
    })
  }
  const logourlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      logourl: e
    })
  }
  const imagechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      image: e
    })
  }
  const discountchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      discount: e
    })
  }

  const handleEdit = (id) => {

    axios.put(`https://api.foodealzapi.com/restaurants/${id}`, {
      name: partnerdata.partnerName,
      mail: partnerdata.partnerMail,
      description: partnerdata.description,
      address: partnerdata.address,
      url: partnerdata.url,
      phone: parseInt(partnerdata.phone),
      latitude: parseFloat(partnerdata.latitude),
      longitude: parseFloat(partnerdata.longitude),
      type: partnerdata.type,
      commission_rate: parseFloat(partnerdata.commission_rate),
      logourl: partnerdata.logourl,
      image: partnerdata.image,
      discount: partnerdata.discount
    })
      .then(() => {
        console.log("done")
      })
      .catch(error => {
        console.log(error);
      });
  }
  const handleClose1 = () => {
    setOpen(false);

  };
  const confirmation = () => {
    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
      <Alert onClconfirmationose={handleClose} severity="success">
        partner is successfully updated!
</Alert>
    </Snackbar>
  };
  const global = (id) => {
    handleEdit(id);
    handleClick();
    confirmation();
    handleClose1();
  }

