import React, {useState, useEffect} from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { useLocation } from "react-router-dom";




const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding :'15px',
 

}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037' ,
  width: '80px',
  borderRadius: '5px'
  
 
 
}
const BasketsUpdate = () => {
const [data, setData] = React.useState({
  error: '',
  data: [],
  temp: [],
  error: null,
 

});
const location = useLocation();
const [basketdata, setBasketdata] = React.useState({
    name: "",
    PriceBeforeDis: "",
    PriceAfterDiscount: "",
    Discount: "",
    Description:"",
    restaurant_id:""
   });
const handleEdit = (id) => {
 
  axios.put('https://api.foodealzapi.com/AllInvendus/${id}', basketdata) 

    .then((basketdata) => {
     
    })
    .catch(error => {
      console.log(error);
    });
}


  return (
    <div >
      <div style={Title1}>
        <h2> Update Basket</h2>
      </div>
      <div style={Field}>
        <TextField
           required id="standard-required" label="name"  defaultValue={location.state.detail.item.name} />

        <TextField style={{ marginLeft: '50px' }}
           required id="standard-required" label="origin price"  defaultValue={location.state.detail.item.PriceBeforeDis} />

      </div >

      <div style={Field}>
        <TextField
           required id="standard-required" label="price after reduction"  defaultValue={location.state.detail.item.PriceAfterDiscount} />
      </div>
      <div style={Field}>
        <TextField
           required id="standard-required" label="discount"  defaultValue={location.state.detail.item.Discount} />

        <TextField style={{ marginLeft: '50px' }}
           required id="standard-required" label="description"  defaultValue={location.state.detail.item.Description} />

        <TextField style={{ marginLeft: '50px' }}
           required id="standard-required" label="restaurant_id"  value={location.state.detail.item.restaurant_id} />
      </div>
      <div style={buttonstyle}>
        <Button variant="outline-success" onClick={handleEdit(location.state.detail.item.restaurant_id)}  >confirmer</Button>
        </div>

    </div>

  )

};

export default BasketsUpdate;
