import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import moment from 'moment';
import config from '../config';


const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '100px',
  width: '300px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}


const Offresadd = (item) => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    activity:'',
    restaurant:null,
    dealselected: '',
    deallist:[],
    dealChoisie:[],
    partnerId: "",
    discountPar: "",
    startingdate:'',
    expireddate:'',
    startinghour:'',
    expiredhour:''


  });
  const handleChange = (event) => {
    const name = event.target.name;
    setData({
      ...data,
      [name]: event.target.value,
    });
  };
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);
  const [open1, setOpen1] = React.useState(false);

  const handleClick = () => {
    setOpen1(true);
  };
  
  const useStyles = makeStyles((theme) => ({
    ligne: {
      display: 'flex',
      flexDirection: 'row',
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 200,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
      width: 200,
    },
  }));


  const [offredata, setOffredata] = React.useState({

    quantity: "",
    nbre_redeemed_deal: "",
    dealid:null,

  });



  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const dealidchange = (e) => {
    setOffredata({
      ...offredata,
      dealid: e
    })
    getDeal(parseInt(e))
  }
  

 
  const changeidpartner = (e) => {
    setData({
      ...data,
      restaurant: e
    })
    getDeal(parseInt(e))

  }

  const ChangeDeal = (e) => {
    let newdata = data.deallist.filter(item =>{return item.description == e})
    setData({
      ...data,
      dealselected: e,
      dealChoisie: newdata,
    
    })
    console.log("item is :",data.dealChoisie[0]);
    setOffredata({
      ...offredata,
      dealid: newdata[0],
    
    })
    
   
    
  }
  
  const getData = async () => {
    const url = `https://api.foodealzapi.com/deals`;
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          offre: res,
          offre1: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };
  const getDeal = async (id) => {
    const url = `https://api.foodealzapi.com/activedeals`;
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        setData({
          ...data,
          deallist: res,
        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  const addOffre = () => {
    console.log( offredata.quantity);

    axios.post(`https://api.foodealzapi.com/deals/dealscheduled`, {
      restaurant_id: parseInt(data.restaurant),
      quantity: parseInt(offredata.quantity),
      nbre_redeemed_deal: parseInt(offredata.nbre_redeemed_deal),
      startingdate: data.startingdate,
      expirydate:data.expireddate,
      startingdate_hours: data.startinghour,
      expirydate_hours: data.expiredhour,
      active: data.activity,
      dealid: parseInt(offredata.dealid),

    })
      .then((res) => {

        getData();
      })
      .catch(error => {
        console.log("l'erreur d'ajout est :",error)

      });

  }
  const handleClose1 = () => {
    setOpen(false);

  };
 
  const global = () => {
    addOffre();
    handleClick();
    handleClose1();




  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen1(false);
  };
  const classes = useStyles();

  return (
    <div >

      <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} variant="outline-success" onClick={() => setOpen(true)} >add Offer </Button>



      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Enter new Offer</DialogTitle>
        <DialogContent>
          
        <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}>restaurant_id :</p>
            <FormControl variant="filled" className={classes.formControl}>
              <InputLabel htmlFor="filled-age-native-simple">partner id</InputLabel>
              <Select
                native
                value={data.restaurant}
                onChange={val => {changeidpartner(val.target.value)}}
                inputProps={{
                  name: 'partnerId',
                  id: 'filled-age-native-simple3',
                }}
              >
                <option aria-label="None" value="" />
                <option value={1}>1:Allora</option>
                <option value={2}>2:Pepita d'oro</option>
                <option value={3}>3:Fel Hokka</option>
                <option value={4}>4:Dapietro</option>
                <option value={5}>5:Dai Savastani by Dapietro</option>
                <option value={6}>6:Elixir Bio</option>
                <option value={7}>7:chez Christy</option>
                <option value={8}>8:Cheesecake</option>
                
              </Select>
            </FormControl>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}>deals list :</p>
            <FormControl variant="filled" className={classes.formControl}>
              <Select
                native
                value={data.dealselected}
                onChange={val => {ChangeDeal(val.target.value)}}
                inputProps={{
                  name: 'partnerId',
                  id: 'filled-age-native-simple3',
                }}
              >
                 <option aria-label="None" value="" />
                {
                  data.deallist.map(item => {
                    return <option value= {item.description}>{item.description}</option>
                  })
                }
                
              </Select>
            </FormControl>
          </div>

          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>quantity :</p>
            <TextField style={Field}
              label="quantity" onChange={e => qtchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>quantity sold :</p>
            <TextField style={Field}
              label="quantity sold" onChange={e => offresoldchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>starting date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date1"
             
                label="starting date"
                type="datetime-local"
                name="starting date"
                value={data.startingdate}
                onChange={event => { console.log(event.target.value),setData({...data,startingdate:event.target.value })}}
                
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          <div className={classes.ligne} >
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>expired date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date2"
                label="expired date"
                type="datetime-local"
                name="expired date"
                defaultValue={data.expireddate}
                onChange={event => { console.log(event.target.value),setData({...data,expireddate:event.target.value })}}
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>starting hour :</p>
            <form className={classes.container} noValidate>
              <TextField
              style={{ marginTop: '40px', marginLeft: '40px' }}
                id="time1"
                label="starting hour"
                type="time"
                defaultValue={data.startinghour}
                onChange={event => { console.log(event.target.value),setData({...data,startinghour:event.target.value })}}
                className={classes.textField}
                minDate="03:00PM"
                maxDate="10:00PM"
                InputLabelProps={{
                  shrink: true,
                }}
                inputProps={{
                  step: 300, // 5 min
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>expired hour :</p>
            <form className={classes.container} noValidate>
              <TextField
              style={{ marginTop: '40px', marginLeft: '40px' }}
                id="time2"
                
                label="expired hour"
                type="time"
                defaultValue={data.expiredhour}
                onChange={event => { console.log(event.target.value),setData({...data,expiredhour:event.target.value })}}
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
                inputProps={{
                  step: 300, // 5 min
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}> active or not :</p>

            <FormControl variant="filled" className={classes.formControl}>
              <InputLabel htmlFor="filled-age-native-simple">active or not</InputLabel>
              <Select
                native
                value={data.activity}
                onChange={handleChange}
                inputProps={{
                  name: 'activity',
                  id: 'filled-age-native-simple',
                }}
              >
                <option aria-label="None" value="" />
                <option value={"active"}>active</option>
                <option value={"not active"}>not active</option>

              </Select>
            </FormControl>
          </div>
         
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }} >deal id :</p>
            <TextField style={Field}
              label="deal id" onChange={e => dealidchange(e.target.value)} variant="filled" value={offredata.dealid}/>
          </div >


        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose1} style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} >
            No
</Button>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={() => global()} > valider </Button>
        </DialogActions>
      </Dialog>
      <Snackbar open={open1} autoHideDuration={1000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        basket is successfully added!
</Alert>
    </Snackbar>




    </div>

  )

};

export default Offresadd;
