import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Link } from "react-router-dom";
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Baskets from "./Menu";
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import Moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { Dialog } from '@material-ui/core';

const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '100px',
  width: '300px',
  marginTop: '25px',
  padding: '15px',

}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'



}


const Menuadd = (item) => {



  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    items: []

  });

  const [basketdata, setBasketdata] = React.useState({

    nom: "",
    image: "",
    PriceBeforeDiscount: "",
    PriceAfterDiscoun: "",
    discount: "",
    description: "",
    deal_description: "",
    restaurant_id: ""

  });

  const nomchange = (e) => {
    setBasketdata({
      ...basketdata,
      nom: e
    })
  }
  //  }
  const imagechange = (e) => {
    setBasketdata({
      ...basketdata,
      image: e
    })
  }
  const pricebeforechange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceBeforeDiscount: e
    })
  }
  const priceafterchange = (e) => {
    setBasketdata({
      ...basketdata,
      PriceAfterDiscoun: e
    })
  }
  const discountchange = (e) => {
    setBasketdata({
      ...basketdata,
      discount: e
    })
  }
  const descriptionchange = (e) => {
    setBasketdata({
      ...basketdata,
      description: e
    })
  }
  const idrestaurantchange = (e) => {
    setBasketdata({
      ...basketdata,
      restaurant_id: e
    })
  }



  const [open, setOpen] = React.useState(false);
  const [open1, setOpen1] = React.useState(false);

  const handleClick = () => {
    setOpen(true);


  };

  const global = () => {
    addBasket();
    handleClick();


  };
  const handleClose1 = () => {
    setOpen(false);

  };
  const confirmation = () => {

    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        basket is successfully added!
</Alert>
    </Snackbar>
  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  const addBasket = () => {
    axios.post('https://api.foodealzapi.com/Invendu/register', {

      nom: basketdata.nom,
      image: basketdata.image,
      PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
      PriceAfterDiscoun: basketdata.PriceAfterDiscoun,
      discount: basketdata.discount,
      description: basketdata.description,
      restaurant_id: basketdata.restaurant_id,


    }

    ).then(() => {
      console.log("ajouter la ligne de nom :");


    })
      .catch(error => {
        console.log(error)
      });
  }





  return (
    <div >
      <Button variant="outline-success" onClick={() => setOpen(true)} >add Menu </Button>
      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Enter new Menu</DialogTitle>
        <DialogContent>
          {/* <DialogContentText>
            hello world
          </DialogContentText> */}

          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>  nom:</p>

            <TextField style={Field}
              label="nom" defaultValue={data.resto} onChange={e => nomchange(e.target.value)} variant="filled" />

          </div>
          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> image :</p>

            <TextField style={Field}
              label="deal_id" defaultValue={data.donner} onChange={e => imagechange(e.target.value)} variant="filled" />

          </div>
          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> price before</p>

            <TextField style={Field}
              label="url de l'image" defaultValue={data.item1} onChange={e => pricebeforechange(e.target.value)} variant="filled" />

          </div>
          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> price after :</p>
            <TextField style={Field}
              label="price before" defaultValue={data.item4} onChange={e => priceafterchange(e.target.value)} variant="filled" />
          </div>
          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> discount :</p>
            <TextField style={Field}
              label="discount" defaultValue={data.item2} onChange={e => discountchange(e.target.value)} variant="filled" />
          </div>

          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> description :</p>
            <TextField style={Field}
              label="price After" defaultValue={data.item3} onChange={e => descriptionchange(e.target.value)} variant="filled" />
          </div>
          <div >
            <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}> id restaurant :</p>
            <TextField style={Field}
              label="decription" defaultValue={data.item5} onChange={e => idrestaurantchange(e.target.value)} variant="filled" />
          </div>




        </DialogContent>
        <DialogActions>
          <Button variant="outline-success" onClick={() => global()} >Valider</Button>


        </DialogActions>
      </Dialog>








    </div>

  )

};

export default Menuadd;
