import React, { useState, useEffect } from "react";


import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import Usersadd from './Usersadd';
import UsersUpdate from './UsersUpdate';
import axios from 'axios';
// import { Dialog } from '@material-ui/core';
// import { TextField } from '@material-ui/core';
// import DialogActions from '@material-ui/core/DialogActions';
// import DialogContent from '@material-ui/core/DialogContent';
// import DialogTitle from '@material-ui/core/DialogTitle';

const Users = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [userdata, setUserdata] = React.useState({
    firstname: "",
    lastname: "",
    mail: "",
    phone: "",
    age: "",
    city: "",
    password: ""
  });
  const [q, setQ] = useState("");

  // const [visible, setVisible] = React.useState(false);

  // const showDialog = () => {
  //   setVisible(true);
  // };
  // const showDialog1 = () => {
  //   setVisible(true);
  // };
  // const handleCancel = () => {
  //   setVisible(false);
  // };
  // const handleEdit = () => {

  //   axios.put('https://api.foodealzapi.com/users', userdata)

  //     .then((userdata) => {
  //       console.log(userdata);
  //     })
  //     .catch(error => {
  //       console.log(error);
  //     });
  // }

  // const addUser = () => {
  //   let userItems = localStorage.getItem('useritem');

  //   console.log(userItems)
  //   axios.post('https://api.foodealzapi.com/users', userItems).then((res) => {

  //     console.log(res.data)
  //   })
  //     .catch(error => {
  //       console.log(error)
  //     });
  //   //   userItems.map((item) => {

  //   // })
  //   // sessioncStorage.setItem('userItems', JSON.stringify([]));

  //   setData({
  //     ...data,

  //   })
  //   // navigation.jumpTo('Mes Couffins')
  //   // navigation.goBack()
  // }



  const CustomTableCell = withStyles(theme => ({
    body: {
      borderBottomColor: 'transparent',
      paddingTop: theme.spacing(1),
      paddingBottom: theme.spacing(1)
    }
  }))(TableCell);

  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/users/${id}`)
      .then(res => {
        res.header ('Access-Control-Allow-Origin','*'),
          
        res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
        console.log(id)
        if (res.data == 'User Deleted!') {
          // handleCancel()
          getData()

        }
      })
      .catch((error) => {
        console.log(error);
      })
  }

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/users';
    let user;
    user = [];
    await fetch(url,{
      crossDomain:true,
      method: 'GET',
      headers: {'Content-Type':'application/json', 'Access-Control-Allow-Origin': 'https://api.foodealzapi.com/'},
    })
      .then(res => res.json())
      .then(res => {
        // res.header ('Access-Control-Allow-Origin','*'),
          
        // res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  useEffect(() => {
    setTimeout(async () => {

      getData()

      // let userid;
      // userid = null;
      // try {
      //   userid = await AsyncStorage.getItem('user_id');
      // } catch (e) {
      //   console.log(e);
      // }

      // console.log('focused section');
    }



      , 500);


  }, [])

  // const searchFilterFunction = (text) => {
  //   const newData = data.temp.filter((item) => {
  //     const itemData = `${item.nom.toUpperCase()}`;
  //     const textData = text.toUpperCase();
  //     return itemData.indexOf(textData) > -1;
  //   });
  //   setData({
  //     ...data,
  //     data: newData
  //   })
  // };
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: '#b4b4b4b4b4',
    marginLeft: '20px',
    marginTop: '20px',

    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'

  }
  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '100px',
    borderRadius: '5px'
  }
  const Field = {
    marginLeft: '20px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


  }
 


  return (
    <div>
      <div style={Title1}>
        <h2>Users</h2>

      </div>

      <div style={buttonstyle}>
        <Link to='/Usersadd'>

        <Button variant="outline-success" >add user </Button>
        </Link>
        

     
      </div>

      <div style={Search1} >
        <input type="text" placeholder=" Search Here..." value={q} onChange={(e) => setQ(e.target.value)} />
      </div>
      <div style={item}>
        <Table>
          <TableHead>
            <TableRow>
              <CustomTableCell style={{ alignItems: 'center' }} >userId</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userFirstname</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userLastname</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userMail</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userPhone</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userAge</CustomTableCell>
              {/* <CustomTableCell style={{ alignItems: 'center' }} align="right">userPassword</CustomTableCell> */}
              <CustomTableCell style={{ alignItems: 'center' }} align="right">userCity</CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right"></CustomTableCell>
              <CustomTableCell style={{ alignItems: 'center' }} align="right"></CustomTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.data.map((item, user_id) => (
              <TableRow key={user_id}>

                <CustomTableCell>

                  {item.user_id}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.firstname}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.lastname}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.mail}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.phone}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.age}
                </CustomTableCell>
                {/* <CustomTableCell align="right">
                  {item.password}
                </CustomTableCell> */}
                <CustomTableCell align="right">
                  {item.ville}
                </CustomTableCell>
                <CustomTableCell align="right">

                 <Link to='/UsersUpdate'>
                    <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" >
                      <EditIcon />
                  
                    </Button>
                    </Link>
                </CustomTableCell>
                <CustomTableCell align="right">
                  <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" onClick={() => this.handleRemove(user_id)}>
                    <ClearIcon />

                  </Button>
                </CustomTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>

      </div>
      <div>

      </div>

    </div>
  )
}
export default Users;
