import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import { useLocation ,BrowserRouter as Router , useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
    fontSize: '50px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '480px',
    justifyContent: 'center'
}
const Field = {
    marginLeft: '400px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


}
const buttonstyle = {
    marginLeft: '530px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '150px',
    borderRadius: '5px'



}
const PartnersUpdate = (itemupdate, props) => {
  const location = useLocation();
    const [data, setData] = React.useState({
        error: '',
        data: [],
        temp: [],
        error: null,
        donner: JSON.stringify(location.state.ligne.item.restaurant_id),
        item1: JSON.stringify(location.state.ligne.item.name),
        item2: JSON.stringify(location.state.ligne.item.mail),
        item3: JSON.stringify(location.state.ligne.item.password),
        item4: JSON.stringify(location.state.ligne.item.typePayment),
        item5: JSON.stringify(location.state.ligne.item.description),
        item6: JSON.stringify(location.state.ligne.item.address),
        item7: JSON.stringify(location.state.ligne.item.url),
        // item8: JSON.parseInt(location.state.ligne.item.phone),
        // // item9: JSON.stringify(location.state.ligne.item.latitude),
        // // item10: JSON.stringify(location.state.ligne.item.longitude),
        // item11: JSON.stringify(location.state.ligne.item.type),
        // item12: JSON.parseInt(location.state.ligne.item.commission_rate),
        // item13: JSON.stringify(location.state.ligne.item.logourl),
        // item14: JSON.stringify(location.state.ligne.item.image),
        // item15: JSON.stringify(location.state.ligne.item.discount)
    });
  

    function Alert(props) {
      return <MuiAlert elevation={6} variant="filled" {...props} />;
    }
    const [open, setOpen] = React.useState(false);
  
    const handleClick = () => {
      setOpen(true);
    };
    const history = useHistory();
    const handleHistory = () =>{
      history.push("/");
    }
    console.log("les items de la page derniÃ¨re est :"+ data.donner);
    const [partnerdata, setPartnerdata] = React.useState({
        partnerName: "",
    partnerMail: "",
    partnerPassword:"",
    typePayment: "",
    description: "",
    address: "",
    url: "",
    phone: "",
    latitude: "",
    longitude: "",
    type: "",
    commission_rate: "",
    logourl: "",
    image: "",
    discount: ""


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
