import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import TableContainer from '@material-ui/core/TableContainer';
import TablePagination from '@material-ui/core/TablePagination';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import { Button, ThemeProvider } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import Menuadd from './Menuadd';

import MenuUpdate from './MenuUpdate';
import { Dialog } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Mapcomponent from "./Mapcomponent";



const Menu = () => {


  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: '',
    item1:JSON.parseInt(item.deal.deal_id),
    item2:JSON.stringify(item.name),
    item3:JSON.stringify(item.deal.imageurl),
    item4:JSON.stringify(item.deal.discount),
    item5:JSON.parseFloat(item.deal.PriceAfterDiscount),
    item6:JSON.parseFloat(item.deal.PriceBeforeDiscount),
    item7:JSON.stringify(item.deal.description),
    item8:JSON.stringify(item.deal.deal_description),
    item9:JSON.stringify(item.deal.startingdate),
    item10:JSON.stringify(item.deal.expirydate),



  });





  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen1(false);
  };


  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  



  const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.white,
      color: theme.palette.common.black,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);


  const useStyles = makeStyles({
    table: {
      minWidth: 400,
      marginTop: 20,
    },
  });

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/restaurants';
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        let object = {
          ...data,
          data: res,
          temp: res,

        };

        setData(
          object
        );
        console.log(object.data)

      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };
  useEffect(() => {
    getData()
  }, [])





  const searchFilterFunction = (text) => {
    const newData = data.temp.filter((item) => {
      const itemData = `${item.deal.deal_id.toUpperCase()}`;
      const textData = text.toUpperCase();
      return itemData.indexOf(textData) > -1;
    });
    console.log(newData)
    setData({
      ...data,
      data: newData
    })
  };
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: 'white',
    marginLeft: '20px',
    marginTop: '20px',
    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'
  }
  const Field = {
    marginLeft: '20px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


  }
  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '100px',
    borderRadius: '5px'
  }

  const handleRemove = async (id) => {
    console.log("iddesigné :" + id)
    try {
      return await axios.delete(`http://192.168.22.78:5000/api/entreroffres/${id}`)
    }

    catch (error) { console.log("deleting error :" + error) }
  }

  const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
      },
    },
  }))(TableRow);

  const [open1, setOpen1] = React.useState(false);

  const handleClick = () => {
    setOpen1(true);
  };
  const handleClose1 = () => {
    setOpen(false);

  };
  const global = async (id) => {
    console.log("clicked :" + id)
    handleClose1();
    handleClick();
    handleRemove(id).then((res) => console.log("sucessfully deleted")).catch((error) => console.log("ilya problème"))

  };



  const classes = useStyles();
  
  return (
    <div>
      <div style={Title1}>
        <h2>Menu</h2>

      </div>
      <div>
        <div style={buttonstyle}>
          <Link to='/Menuadd'>
            <Button variant="outline-success"  >add Item </Button>
          </Link>
        </div>
        <div style={Search1} >
          <input type="text" placeholder=" Search Here..."
            contentEditable={true}
            onChange={e => searchFilterFunction(e.target.value)} />
        </div>
        <div style={item}>
          <Paper >
            <TableContainer >
              <Table className={classes.table}>
                <TableHead>
                  <TableRow>
                    <StyledTableCell align=""> deal_id</StyledTableCell>
                    <StyledTableCell align="right">restaurant name</StyledTableCell>
                    <StyledTableCell align="right">image url</StyledTableCell>
                    <StyledTableCell align="right">discount</StyledTableCell>
                    <StyledTableCell align="right">Price After</StyledTableCell>
                    <StyledTableCell align="right">Price Before</StyledTableCell>
                    <StyledTableCell align="right">description</StyledTableCell>
                    <StyledTableCell align="right">deal_description</StyledTableCell>
                    <StyledTableCell align="right">starting date</StyledTableCell>
                    <StyledTableCell align="right">expirydate</StyledTableCell>
                    
                    <StyledTableCell></StyledTableCell>
                    <StyledTableCell></StyledTableCell>
                    
                  </TableRow>
                </TableHead>
                <TableBody>
                  {data.data.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((item, id) =>{
                       console.log("les descriptions des deals sont : "+JSON.stringify(item.deal.description));
                  return (
                 



                    <StyledTableRow hover role="checkbox" key={item.deal.deal_id}>

                      <StyledTableCell >

                        {item1}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item2}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item3}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item4}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item5}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item6}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item7}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item8}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item9}
                      </StyledTableCell >
                      <StyledTableCell align="right">
                        {item10}
                      </StyledTableCell >




                      <StyledTableCell align="right">
                       

                        <Link to={{
                          pathname: '/MenuUpdate',

                          state: { ligne: { item } }
                        }} >

                          <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success"  >
                            <EditIcon />

                          </Button>
                        </Link>

                      </StyledTableCell>
                      
                      

                      <Mapcomponent item={item.entreroffres_id} handleClose={handleClose} handleClose1={handleClose1} />

                      </StyledTableRow >
);
                      }
                  )}




                </TableBody>
              </Table>
            </TableContainer>
              <TablePagination
                rowsPerPageOptions={[10, 25, 100]}
                component="div"
                count={data.data.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
              />
          </Paper>
        </div>
          <div>

          </div>

        </div>
      </div >
  )
}
