        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>phone :</p>
          <TextField style={Field}
            label="phone" defaultValue={data.item8} onChange={e => phonechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>latitude :</p>
          <TextField style={Field}
            label="latitude" defaultValue={data.item9} onChange={e => latitudechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>longitude :</p>
          <TextField style={Field}
            label="longitude" defaultValue={data.item10} onChange={e => longitudechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>type :</p>
          <TextField style={Field}
            label="type" defaultValue={data.item11} onChange={e => typechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>commission rate :</p>
          <TextField style={Field}
            label="commission_rate" defaultValue={data.item12} onChange={e => commission_ratechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>logo :</p>
          <TextField style={Field}
            label="logo url" defaultValue={data.item13} onChange={e => logourlchange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>image :</p>
          <TextField style={Field}
            label="image" defaultValue={data.item14} onChange={e => imagechange(e.target.value)} variant="filled" />
        </div>
        <div>
          <p style={{ fontSize: '20px', color: "#008037", fontWeight: "bold" }}>discount :</p>
          <TextField style={Field}
            label="discount" defaultValue={data.item15} onChange={e => discountchange(e.target.value)} variant="filled" />
        </div>


        <Button style={buttonstyle} onClick={() => global(data.donner)} >
          confirmer
                    </Button>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            partner is successfully updated!
</Alert>
        </Snackbar>

      </div>
    </div>
  )

};

export default PartnersUpdate;
