import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';




const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}


const Offresadd = () => {
 
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,


  });
  const location = useLocation();
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }

 
  const [offredata, setOffredata] = React.useState({
    quantity: "",
    nbre_redeemed_deal: "",
    startingdate: "",
    expirydate: "",
    startingdate_hours: "",
    expirydate_hours: "",
    active: "",
   



  });

 

  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const startingdatechange = (e) => {
    setOffredata({
      ...offredata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setOffredata({
      ...offredata,
      expirydate: e
    })
  }
  const startingdate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      startingdate_hours: e
    })
  }
  const expirydate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      expirydate_hours: e
    })
  }
  const activechange = (e) => {
    setOffredata({
      ...offredata,
      active: e
    })
  }

 
  


  const addOffre = () => {
    console.log(offredata.id, offredata.quantity);

    axios.post('https://api.foodealzapi.com/deals/dealcreate', {
      
      quantity: parseInt(offredata.quantity),
      nbre_redeemed_deal: parseInt(offredata.nbre_redeemed_deal),
      startingdate: offredata.startingdate,
      expirydate: offredata.expirydate,
      startingdate_hours: offredata.startingdate_hours,
      expirydate_hours: offredata.expirydate_hours,
      active: offredata.active,
      
     
    })
      .then(() => {

        console.log("done")
      })
      .catch(error => {
        console.log(error)

      });

  }
  const global = () => {
    addOffre();
    handleClick();
    handleHistory();

    


  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };


  return (
    <div >
     
      <h2 style={Title1} >Enter new deal</h2>

      <div >
        <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold" , marginLeft:'100px'}}>quantity :</p>
        <TextField style={Field}
          label="quantity" onChange={e => qtchange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}>quantity sold :</p>
        <TextField style={Field}
          label="quantity sold" onChange={e => offresoldchange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}>starting date :</p>
        <TextField style={Field}
          label="starting date" onChange={e => startingdatechange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}>expired date :</p>
        <TextField style={Field}
          label="expiry date" onChange={e => expirydatechange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}>starting hour :</p>
        <TextField style={Field}
          label="starting hour" onChange={e => startingdate_hourschange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}>expired hour :</p>
        <TextField style={Field}
          label="expiry date" onChange={e => expirydate_hourschange(e.target.value)} variant="filled" />
      </div>
      <div >
      <p style={{fontSize: '20px', color:"#008037", fontWeight: "bold", marginLeft:'100px'}}> active or not :</p>
        <TextField style={Field}
          label="active or not" onChange={e => activechange(e.target.value)} variant="filled" />
      </div>
      



      <Button style={buttonstyle} onClick={() => global()} > valider </Button>
      <Snackbar open={open} autoHideDuration={2000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          deal is successfully added!
</Alert>
      </Snackbar>



    </div>

  )

};

export default Offresadd;
