import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { useLocation } from "react-router-dom";


const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '550px',
    justifyContent: 'center'
}
const Field = {
    marginLeft: '400px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


}
const buttonstyle = {
    marginLeft: '600px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '80px',
    borderRadius: '5px'



}
const PartnersUpdate = (itemupdate, props) => {
    const [data, setData] = React.useState({
        error: '',
        data: [],
        temp: [],
        error: null,
    });
    const location = useLocation();
    // const [visible, setVisible] = React.useState(false);
    // const handleCancel = () => {
    //     setVisible(false);
    // };
    const [partnerdata, setPartnerdata] = React.useState({
        partnerName: "",
    partnerMail: "",
    typePayment: "",
    description: "",
    address: "",
    url: "",
    phone: "",
    latitude: "",
    longitude: "",
    type: "",
    commission_rate: "",
    logourl: "",
    image: "",
    discount: ""


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
    })
  }

  const mailchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerMail: e
    })
  }
  const typePaymentchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      typePayment: e
    })
  }
  const descriptionchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      description: e
    })
  }
  const addresschange = (e) => {
    setPartnerdata({
      ...partnerdata,
      address: e
    })
  }
  const urlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      url: e
    })
  }
  const phonechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      phone: e
    })
  }
  const latitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      latitude: e
    })
  }
  const longitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      longitude: e
    })
  }
  const typechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      type: e
    })
  }
  const commission_ratechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      commission_rate: e
    })
  }
  const logourlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      logourl: e
    })
  }
  const imagechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      image: e
    })
  }
  const discountchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      discount: e
    })
  }

    const handleEdit = (id) => {

        axios.put(`https://api.foodealzapi.com/restaurants/${id}`, {
            name: partnerdata.partnerName,
            mail: partnerdata.partnerMail,
            typePayment: partnerdata.typePayment,
            description: partnerdata.description,
            address: partnerdata.address,
            url: partnerdata.url,
            phone: parseInt(partnerdata.phone),
            latitude: partnerdata.latitude,
            longitude: partnerdata.longitude,
            type: partnerdata.type,
            commission_rate: partnerdata.commission_rate,
            logourl: partnerdata.logourl,
            image: partnerdata.image,
            discount: partnerdata.discount
        })
            .then(() => {
                console.log("done")
            })
            .catch(error => {
                console.log(error);
            });
    }

    console.log(location.state)

    return (
        <div >
            <div >
                <h2 style={Title1} >Update Partner</h2>

                <div style={Field}>
        <TextField
          label="name" onChange={e => namechange(e.target.value)} />

        <TextField style={{ marginLeft: '150px' }}
          label="mail" onChange={e => mailchange(e.target.value)} />
        <TextField style={{ marginLeft: '20px' }}
          label="type de paiement" onChange={e => typePaymentchange(e.target.value)} />
        <TextField style={{ marginLeft: '100px'  }}
          label="description" onChange={e => descriptionchange(e.target.value)} />
          </div>
          <div style={Field}>
        <TextField 
          label="address" onChange={e => addresschange(e.target.value)} />
        <TextField style={{ marginLeft: '150px' }}
          label="url de site web" onChange={e => urlchange(e.target.value)} />
        <TextField style={{ marginLeft: '50px' }}
          label="phone" onChange={e => phonechange(e.target.value)} />
        <TextField style={{ marginLeft: '50px' }}
          label="latitude" onChange={e => latitudechange(e.target.value)} />
          </div>
          <div style={Field}>
        <TextField 
          label="longitude" onChange={e => longitudechange(e.target.value)} />
        <TextField style={{ marginLeft: '150px' }}
          label="type" onChange={e => typechange(e.target.value)} />
        <TextField style={{ marginLeft: '20px' }}
          label="commission_rate" onChange={e => commission_ratechange(e.target.value)} />
        <TextField style={{ marginLeft: '100px' }}
          label="logo url" onChange={e => logourlchange(e.target.value)} />
          </div>
          <div style={Field}>
        <TextField 
          label="image" onChange={e => imagechange(e.target.value)} />
        <TextField style={{ marginLeft: '150px' }}
          label="discount" onChange={e => discountchange(e.target.value)} />
          </div>


                <Button style={buttonstyle} onClick={() => handleEdit(location.state.detail.item.restaurant_id)} >
                    confirmer
                    </Button>

            </div>
        </div>
    )

};

export default PartnersUpdate;
