import React , {useState, useEffect} from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { useLocation } from "react-router-dom";



const Title1 = {
  fontSize: '20px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '20px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '20px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '150px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '80px',
  borderRadius: '5px'



}

const Basketsadd = () => {

const [data, setData] = React.useState({
  error: '',
  data: [],
  temp: [],
  error: null,
  items: []

});
const location = useLocation();
const [basketdata, setBasketdata] = React.useState({
  nom: "",
  PriceBeforeDiscount: "",
  PriceAfterDiscoun: "",
  Discount: "",
  Description:"",
  restaurant_id:""
});
const namechange = (e) => {
  setBasketdata({
    ...basketdata,
    nom: e
  })
}
const beforedischange = (e) => {
  setBasketdata({
    ...basketdata,
    PriceBeforeDiscount: e
  })
}
const afterdischange = (e) => {
  setBasketdata({
    ...basketdata,
    PriceAfterDiscoun: e
  })
}
const discountchange = (e) => {
  setBasketdata({
    ...basketdata,
    Discount: e
  })
}
const descriptionchange = (e) => {
  setBasketdata({
    ...basketdata,
    Description: e
  })
}
const restidchange = (e) => {
  setBasketdata({
    ...basketdata,
    restaurant_id: e
  })
}
const addBasket =  () => {
  axios.post('http://192.168.22.205:5000//api/entreroffres/register', {
    nom: basketdata.nom,
    PriceBeforeDiscount: basketdata.PriceBeforeDiscount,
    PriceAfterDiscoun: basketdata.PriceAfterDiscoun,
    discount: basketdata.Discount,
    description: basketdata.Description,
    restaurant_id: basketdata.restaurant_id
  }
  
  ).then(() => {
    console.log("done");
    
    
  })
    .catch(error => {
        console.log(error)
    });
  }





  return (
    <div >
      <div style={Title1}>
        <h2> Enter new Basket</h2>
      </div>
      <div style={Field}>
      <TextField
        label="nom"  onChange={e=>namechange(e.target.value)} />

      <TextField style={{ marginLeft: '50px' }}
        label="origin price"  onChange={e=>beforedischange(e.target.value)} />

    </div >

    <div style={Field}>
      <TextField
       label="price after reduction"  onChange={e=>afterdischange(e.target.value) } />
    </div>
    <div style={Field}>
      <TextField
         label="discount"  onChange={e=>discountchange(e.target.value) }  />

      <TextField style={{ marginLeft: '50px' }}
         label="description"  onChange={e=>descriptionchange(e.target.value) } />

      <TextField style={{ marginLeft: '50px' }}
         label="restaurant_id"  onChange={e=>restidchange(e.target.value) } />
    </div>



      <div style={buttonstyle}>
        <Button variant="outline-success" onClick={() =>addBasket()} >Valider</Button>
      </div>

    </div>

  )

};

export default Basketsadd;
