import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { useLocation } from "react-router-dom";


const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
}
const Field = {
    marginLeft: '20px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


}
const buttonstyle = {
    marginLeft: '150px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '80px',
    borderRadius: '5px'



}
const PartnersUpdate = (itemupdate, props) => {
    const [data, setData] = React.useState({
        error: '',
        data: [],
        temp: [],
        error: null,
    });
    const location = useLocation();
    // const [visible, setVisible] = React.useState(false);
    // const handleCancel = () => {
    //     setVisible(false);
    // };
    const [partnerdata, setPartnerdata] = React.useState({
        partnerName: location.state.detail.item.name,
        partnerMail: location.state.detail.item.mail,
        phone: location.state.detail.item.phone,
        city: location.state.detail.item.address

    });

    const namechange = (e) =>{
        setPartnerdata({
            ...partnerdata,
            partnerName: e
        })
    }
    const mailchange = (e) =>{
        setPartnerdata({
            ...partnerdata,
            partnerMail: e
        })
    }
    const phonechange = (e) =>{
        setPartnerdata({
            ...partnerdata,
            phone: e
        })
    }
    const addresschange = (e) =>{
        setPartnerdata({
            ...partnerdata,
            city: e
        })
    }
    const handleEdit = (id) => {

        axios.put(`https://api.foodealzapi.com/restaurants/${id}`, {
            name: partnerdata.partnerName,
            mail: partnerdata.partnerMail,
            phone:  parseInt(partnerdata.phone),
            address: partnerdata.city,
        })
            .then(() => {
                console.log("done")
            })
            .catch(error => {
                console.log(error);
            });
    }

    console.log(location.state)

    return (
        <div >
            <div >
                <h2 style={Title1} >Update Partner</h2>

                <div style={Field}>
                    <TextField
                        required id="standard-required" label="name" defaultValue={partnerdata.partnerName} onChange={e=>namechange(e.target.value)} />

                    <TextField style={{ marginLeft: '50px' }}
                        required id="standard-required" label="mail" defaultValue={partnerdata.partnerMail} onChange={e=>mailchange(e.target.value)} />

                </div >
                <div style={Field}>
                    <TextField
                        required id="standard-required" label="phone" defaultValue={partnerdata.phone} onChange={e=>phonechange(e.target.value)} />


                    <TextField style={{ marginLeft: '50px' }}
                        required id="standard-required" label="address" defaultValue={partnerdata.city} onChange={e=>addresschange(e.target.value)} />
                </div>

                {/* <div >
                    <TextField style={{ marginTop: '20px', marginLeft: '25px' }}
                        value={location.state.detail.item.password} />
                </div> */}


                <Button style={buttonstyle} onClick={() => handleEdit(location.state.detail.item.restaurant_id)} >
                    confirmer
                    </Button>

            </div>
        </div>
    )

};

export default PartnersUpdate;
