import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import { useLocation ,BrowserRouter as Router , useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
    fontSize: '50px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '480px',
    justifyContent: 'center'
}
const Field = {
    marginLeft: '400px',
    width: '500px',
    marginTop: '25px',
    padding: '15px',


}
const buttonstyle = {
    marginLeft: '530px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '150px',
    borderRadius: '5px'



}
const PartnersUpdate = (itemupdate, props) => {
    const [data, setData] = React.useState({
        error: '',
        data: [],
        temp: [],
        error: null,
    });
    const location = useLocation();

    function Alert(props) {
      return <MuiAlert elevation={6} variant="filled" {...props} />;
    }
    const [open, setOpen] = React.useState(false);
  
    const handleClick = () => {
      setOpen(true);
    };
    const history = useHistory();
    const handleHistory = () =>{
      history.push("/");
    }
    // const [visible, setVisible] = React.useState(false);
    // const handleCancel = () => {
    //     setVisible(false);
    // };
    const [partnerdata, setPartnerdata] = React.useState({
        partnerName: "",
    partnerMail: "",
    partnerPassword:"",
    typePayment: "",
    description: "",
    address: "",
    url: "",
    phone: "",
    latitude: "",
    longitude: "",
    type: "",
    commission_rate: "",
    logourl: "",
    image: "",
    discount: ""


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
    })
  }

  const mailchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerMail: e
    })
  }
  const passwordchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerPassword: e
    })
  }
  const typePaymentchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      typePayment: e
    })
  }
  const descriptionchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      description: e
    })
  }
  const addresschange = (e) => {
    setPartnerdata({
      ...partnerdata,
      address: e
    })
  }
  const urlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      url: e
    })
  }
  const phonechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      phone: e
    })
  }
  const latitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      latitude: e
    })
  }
  const longitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      longitude: e
    })
  }
  const typechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      type: e
    })
  }
  const commission_ratechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      commission_rate: e
    })
  }
  const logourlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      logourl: e
    })
  }
  const imagechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      image: e
    })
  }
  const discountchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      discount: e
    })
  }

    const handleEdit = (id) => {

        axios.put(`https://api.foodealzapi.com/restaurants/${id}`, {
            name: partnerdata.partnerName,
            mail: partnerdata.partnerMail,
            password: partnerdata.partnerPassword,
            typePayment: partnerdata.typePayment,
            description: partnerdata.description,
            address: partnerdata.address,
            url: partnerdata.url,
            phone: parseInt(partnerdata.phone),
            // latitude: partnerdata.latitude,
            // longitude: partnerdata.longitude,
            type: partnerdata.type,
            commission_rate: parseInt(partnerdata.commission_rate),
            logourl: partnerdata.logourl,
            image: partnerdata.image,
            discount: partnerdata.discount
        })
            .then(() => {
                console.log("done")
            })
            .catch(error => {
                console.log(error);
            });
