import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import DialogActions from '@material-ui/core/DialogActions';
import MuiAlert from '@material-ui/lab/Alert';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { withStyles, makeStyles } from '@material-ui/core/styles';




const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '100px',
  width: '300px',
  marginTop: '25px',
  padding: '15px',
}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}

const useStyles = makeStyles((theme) => ({
  ligne: {
    display: 'flex',
    flexDirection: 'row',
  },
}));

const Partnersadd = (item) => {
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    partnerItems: []

  });

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }

  const location = useLocation();
  const [partnerdata, setPartnerdata] = React.useState({
    partnerName: "",
    partnerMail: "",
    description: "",
    address: "",
    url: "",
    phone: "",
    latitude: "",
    longitude: "",
    type: "",
    commission_rate: "",
    logourl: "",
    image: "",
    discount: ""


  });

  const namechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerName: e
    })
  }

  const mailchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      partnerMail: e
    })
  }

  const descriptionchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      description: e
    })
  }
  const addresschange = (e) => {
    setPartnerdata({
      ...partnerdata,
      address: e
    })
  }
  const urlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      url: e
    })
  }
  const phonechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      phone: e
    })
  }
  const latitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      latitude: e
    })
  }
  const longitudechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      longitude: e
    })
  }
  const typechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      type: e
    })
  }
  const commission_ratechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      commission_rate: e
    })
  }
  const logourlchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      logourl: e
    })
  }
  const imagechange = (e) => {
    setPartnerdata({
      ...partnerdata,
      image: e
    })
  }
  const discountchange = (e) => {
    setPartnerdata({
      ...partnerdata,
      discount: e
    })
  }


  const addPartner = () => {
    console.log(partnerdata.partnerPassword)
    axios.post('https://api.foodealzapi.com/restaurants/register', {
      name: partnerdata.partnerName,
      mail: partnerdata.partnerMail,
      description: partnerdata.description,
      address: partnerdata.address,
      url: partnerdata.url,
      phone: parseInt(partnerdata.phone),
      latitude: parseFloat(partnerdata.latitude),
      longitude: parseFloat(partnerdata.longitude),
      type: partnerdata.type,
      commission_rate: parseFloat(partnerdata.commission_rate),
      logourl: partnerdata.logourl,
      image: partnerdata.image,
      discount: partnerdata.discount
    })

      .then(() => {

        console.log("done")
      })
      .catch(error => {
        console.log(" problem in posting " + error)

      });

  }
  const confirmation = () => {
    <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        partner is successfully added!
</Alert>
    </Snackbar>

  };
  const handleClose1 = () => {
    setOpen(false);

  };
  const global = () => {
    addPartner();
    handleClick();
    confirmation();
    handleClose1();

  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  const classes = useStyles();

  return (
    <div >
      <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} variant="outline-success" onClick={() => setOpen(true)} >add Partner </Button>
      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Enter new partner</DialogTitle>
        <DialogContent>
        <div className={classes.ligne}>
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop:'10px' }}>name :</p>
        <TextField style={Field}
          label="name" onChange={e => namechange(e.target.value)} variant="filled" />
      </div>
      <div className={classes.ligne}>
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>mail :</p>
        <TextField style={Field}
          label="mail" onChange={e => mailchange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>password :</p>
        <TextField style={Field}
          label="password" onChange={e => passwordchange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold"}}>type de paiement :</p>
        <TextField style={Field}
          label="type de paiement" onChange={e => typePaymentchange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>description :</p>
        <TextField style={Field}
          label="description" onChange={e => descriptionchange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>address :</p>
        <TextField style={Field}
          label="address" onChange={e => addresschange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>site web :</p>
        <TextField style={Field}
          label="url de site web" onChange={e => urlchange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>phone :</p>
        <TextField style={Field}
          label="phone" onChange={e => phonechange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>latitude :</p>
        <TextField style={Field}
          label="latitude" onChange={e => latitudechange(e.target.value)} variant="filled" />
      </div>

      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>longitude :</p>
        <TextField style={Field}
          label="longitude" onChange={e => longitudechange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>type :</p>
        <TextField style={Field}
          label="type" onChange={e => typechange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>commission rate :</p>
        <TextField style={Field}
          label="commission_rate" onChange={e => commission_ratechange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>logo :</p>
        <TextField style={Field}
          label="logo url" onChange={e => logourlchange(e.target.value)} variant="filled" />
      </div>

      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>image :</p>
        <TextField style={Field}
          label="image" onChange={e => imagechange(e.target.value)} variant="filled" />
      </div>
      <div >
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold" }}>discount :</p>
        <TextField style={Field}
          label="discount" onChange={e => discountchange(e.target.value)} variant="filled" />
      </div>

        </DialogContent>
        <DialogActions>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={handleClose1} >
            No
</Button>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={() => global()} > valider </Button>
        </DialogActions>
      </Dialog>




     










    </div>

  )

};

export default Partnersadd;
