import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from 'axios';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';


const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '400px',
  width: '500px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'



}
const OffresUpdate = (itemupdate, props) => {
  const location = useLocation();
  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    donner: JSON.stringify(location.state.ligne.item.id),
    item1: JSON.stringify(location.state.ligne.item.quantity),
    item2: JSON.stringify(location.state.ligne.item.nbre_redeemed_deal),
    item3: JSON.stringify(location.state.ligne.item.startingdate),
    item4: JSON.stringify(location.state.ligne.item.expirydate),
    item5: JSON.stringify(location.state.ligne.item.startingdate_hours),
    item6: JSON.stringify(location.state.ligne.item.expirydate_hours),
    item7: JSON.stringify(location.state.ligne.item.active),
    item8: JSON.stringify(location.state.ligne.item.restaurant_id),
    item9: JSON.stringify(location.state.ligne.item.deal_id),
  });
  

  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  console.log("les items de la page derniÃ¨re est :"+ data.donner);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }
  // const [visible, setVisible] = React.useState(false);
  // const handleCancel = () => {
  //     setVisible(false);
  // };
  const [offredata, setOffredata] = React.useState({
    
    quantity: data.item1,
    nbre_redeemed_deal: data.item2,
    startingdate: data.item3,
    expirydate: data.item4,
    startingdate_hours: data.item5,
    expirydate_hours: data.item6,
    active: data.item7,
    



  });

  

  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const startingdatechange = (e) => {
    setOffredata({
      ...offredata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setOffredata({
      ...offredata,
      expirydate: e
    })
  }
  const startingdate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      startingdate_hours: e
    })
  }
  const expirydate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      expirydate_hours: e
    })
  }
  const activechange = (e) => {
    setOffredata({
      ...offredata,
      active: e
    })
  }
 

  const handleEdit = (id) => {

    axios.put(`https://api.foodealzapi.com/dealsscheduled/${id}`, {
     
      quantity: parseInt(offredata.quantity),
      nbre_redeemed_deal: parseInt(offredata.nbre_redeemed_deal),
      startingdate: offredata.startingdate,
      expirydate: offredata.expirydate,
      startingdate_hours: offredata.startingdate_hours,
      expirydate_hours: offredata.expirydate_hours,
      active: offredata.active,
     
    })
      .then(() => {
        console.log("done")
      })
      .catch(error => {
        console.log(error);
      });
  }
  const global = (id) => {
    handleEdit(id);
    handleClick();
    handleHistory();
  }
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  console.log(location.state)

  return (
    <div >
      <div >
       
        <h2 style={Title1} >Update deal</h2>

        
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>quantity :</p>
          <TextField style={Field}
            label="quantity" defaultValue={data.item1} onChange={e => qtchange(e.target.value)} variant="filled" />
            </div>
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>quantity sold :</p>
          <TextField style={Field}
            label="quantity sold" defaultValue={data.item2} onChange={e => offresoldchange(e.target.value)} variant="filled"/>
            </div>
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}> starting date :</p>
          <TextField style={Field}
            label="starting date" defaultValue={data.item3} onChange={e => startingdatechange(e.target.value)} variant="filled"/>
        </div>
        <div >
        <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>expired date :</p>
          <TextField style={Field}
            label="expiry date" defaultValue={data.item4} onChange={e => expirydatechange(e.target.value)} variant="filled"/>
            </div>
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>starting hour :</p>
          <TextField style={Field}
            label="starting hour" defaultValue={data.item5} onChange={e => startingdate_hourschange(e.target.value)} variant="filled"/>
            </div>
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>expired hour :</p>
          <TextField style={Field}
            label="expiry hour" defaultValue={data.item6} onChange={e => expirydate_hourschange(e.target.value)} variant="filled" />
            </div>
            <div >
            <p style={{fontSize: '20px', color: "#008037", fontWeight: "bold", marginLeft:'100px'}}>active or not :</p>
          <TextField style={Field}
            label="active or not" defaultValue={data.item7} onChange={e => activechange(e.target.value)} variant="filled"/>
            </div>
            


        <Button style={buttonstyle} onClick={() => global(data.donner)} >
          confirmer
                    </Button>
        <Snackbar open={open} autoHideDuration={2000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            deal is successfully updated!
</Alert>
        </Snackbar>

      </div>
    </div>
  )

};

export default OffresUpdate;
