import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { Dialog } from '@material-ui/core';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import { ContactSupportOutlined } from '@material-ui/icons';
import { useLocation, BrowserRouter as Router, useHistory } from "react-router-dom";
import { withStyles, makeStyles } from '@material-ui/core/styles';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';



const Title1 = {
  fontSize: '30px',
  color: '#008037',
  fontWeight: 'bold',
  marginLeft: '480px',
  justifyContent: 'center'
}
const Field = {
  marginLeft: '100px',
  width: '300px',
  marginTop: '25px',
  padding: '15px',


}
const buttonstyle = {
  marginLeft: '530px',
  marginTop: '50px',
  backgroundColor: '#008037',
  width: '120px',
  borderRadius: '5px'

}


const Offresadd = (item) => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,


  });
  const location = useLocation();
  function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };
  const history = useHistory();
  const handleHistory = () => {
    history.push("/");
  }
  const useStyles = makeStyles((theme) => ({
    ligne: {
      display: 'flex',
      flexDirection: 'row',
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 200,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
      width: 200,
    },
  }));


  const [offredata, setOffredata] = React.useState({

    quantity: "",
    nbre_redeemed_deal: "",
    startingdate: "",
    expirydate: "",
    startingdate_hours: "",
    expirydate_hours: "",
    active: "",
    restaurant_id: "",
    deal_id: ""



  });



  const qtchange = (e) => {
    setOffredata({
      ...offredata,
      quantity: e
    })
  }
  const offresoldchange = (e) => {
    setOffredata({
      ...offredata,
      nbre_redeemed_deal: e
    })
  }
  const startingdatechange = (e) => {
    setOffredata({
      ...offredata,
      startingdate: e
    })
  }
  const expirydatechange = (e) => {
    setOffredata({
      ...offredata,
      expirydate: e
    })
  }
  const startingdate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      startingdate_hours: e
    })
  }
  const expirydate_hourschange = (e) => {
    setOffredata({
      ...offredata,
      expirydate_hours: e
    })
  }
  const activechange = (e) => {
    setOffredata({
      ...offredata,
      active: e
    })
  }

  const restaurant_idchange = (e) => {
    setOffredata({
      ...offredata,
      restaurant_id: e
    })
  }
  const deal_idchange = (e) => {
    setOffredata({
      ...offredata,
      deal_id: e
    })
  }



  const addOffre = () => {
    console.log(offredata.id, offredata.quantity);

    axios.post('https://api.foodealzapi.com/deals/dealcreate', {

      quantity: parseInt(offredata.quantity),
      nbre_redeemed_deal: parseInt(offredata.nbre_redeemed_deal),
      startingdate: offredata.startingdate,
      expirydate: offredata.expirydate,
      startingdate_hours: offredata.startingdate_hours,
      expirydate_hours: offredata.expirydate_hours,
      active: offredata.active,
      restaurant_id: parseInt(offredata.restaurant_id),
      deal_id: parseInt(offredata.deal_id),

    })
      .then(() => {

        console.log("done")
      })
      .catch(error => {
        console.log(error)

      });

  }
  const handleClose1 = () => {
    setOpen(false);

  };
  const confirmation = () => {
    <Snackbar open={open1} autoHideDuration={2000} onClose={handleClose}>
      <Alert onClose={handleClose} severity="success">
        basket is successfully deleted!
</Alert>
    </Snackbar>
  };
  const global = () => {
    addOffre();
    handleClick();
    confirmation();
    handleClose1();




  };
  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };
  const classes = useStyles();

  return (
    <div >

      <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} variant="outline-success" onClick={() => setOpen(true)} >add Menu </Button>



      <Dialog
        open={open}
        onClose={handleClose1}

      >
        <DialogTitle >Enter new Offer</DialogTitle>
        <DialogContent>

          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>quantity :</p>
            <TextField style={Field}
              label="quantity" onChange={e => qtchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>quantity sold :</p>
            <TextField style={Field}
              label="quantity sold" onChange={e => offresoldchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>starting date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date"
                label="starting date"
                type="date"
                defaultValue="2017-05-24"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          <div className={classes.ligne} >
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>expired date :</p>
            <form className={classes.container} noValidate>
              <TextField
                style={{ marginTop: '40px', marginLeft: '40px' }}
                id="date"
                label="expired date"
                type="date"
                defaultValue="2017-05-24"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>starting hour :</p>
            <form className={classes.container} noValidate>
              <TextField
                id="time"
                label="starting hour"
                type="time"
                defaultValue="07:30"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
                inputProps={{
                  step: 300, // 5 min
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>expired hour :</p>
            <form className={classes.container} noValidate>
              <TextField
                id="time"
                label="expired hour"
                type="time"
                defaultValue="07:30"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
                inputProps={{
                  step: 300, // 5 min
                }}
              />
            </form>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '30px' }}> active or not :</p>

            <FormControl variant="filled" className={classes.formControl}>
              <InputLabel htmlFor="filled-age-native-simple">active or not</InputLabel>
              <Select
                native
                value={offredata.active}
                onChange={activechange}
                inputProps={{
                  name: 'active',
                  id: 'filled-age-native-simple',
                }}
              >
                <option aria-label="None" value="" />
                <option value={10}>active</option>
                <option value={20}>not active</option>

              </Select>
            </FormControl>
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }} >restaurant id :</p>
            <TextField style={Field}
              label="restaurant id" onChange={e => restaurant_idchange(e.target.value)} variant="filled" />
          </div>
          <div className={classes.ligne}>
            <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }} >deal id :</p>
            <TextField style={Field}
              label="deal id" onChange={e => deal_idchange(e.target.value)} variant="filled" />
          </div >


        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose1} style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} >
            No
</Button>
          <Button style={{ backgroundColor: '#008037', borderRadius: '5px', marginLeft: '20px' }} onClick={() => global()} > valider </Button>
        </DialogActions>
      </Dialog>





    </div>

  )

};

export default Offresadd;
