import React, { useState, useEffect } from "react";
// import {
//  UsersCard
// } from "./pages/Users";

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import { Link } from "react-router-dom";
import { withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import EditIcon from '@material-ui/icons/Edit';
import ClearIcon from '@material-ui/icons/Clear';
import axios from 'axios';
import Basketsadd from './Basketsadd';
import BasketsUpdate from './BasketsUpdate';


const Baskets = () => {

  const [data, setData] = React.useState({
    error: '',
    data: [],
    temp: [],
    error: null,
    search: null,


  });
  const [q, setQ] = useState("");


  const CustomTableCell = withStyles(theme => ({
    body: {
      borderBottomColor: 'transparent',
      paddingTop: theme.spacing(1),
      paddingBottom: theme.spacing(1)
    }
  }))(TableCell);

  const getData = async () => {
    const url = 'https://api.foodealzapi.com/Invendus/4';
    let user;
    user = [];
    await fetch(url)
      .then(res => res.json())
      .then(res => {
        console.log(res)
        setData({
          ...data,
          data: res,
          temp: res,

        });
      })
      .catch(error => {
        setData({
          ...data,
          error: 'Error Loading content',
        })
      })
  };

  useEffect(() => {
    setTimeout(async () => {

      getData()

      // let userid;
      // userid = null;
      // try {
      //   userid = await AsyncStorage.getItem('user_id');
      // } catch (e) {
      //   console.log(e);
      // }

      // console.log('focused section');
    }



      , 500);


  }, [])

  // const searchFilterFunction = (text) => {
  //   const newData = data.temp.filter((item) => {
  //     const itemData = `${item.nom.toUpperCase()}`;
  //     const textData = text.toUpperCase();
  //     return itemData.indexOf(textData) > -1;
  //   });
  //   setData({
  //     ...data,
  //     data: newData
  //   })
  // };
  const Title1 = {
    fontSize: '20px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '20px',
    justifyContent: 'center'
  }

  const Search1 = {

    backgroundColor: 'white',
    marginLeft: '20px',
    marginTop: '20px',
    borderBottomWidth: 1,
    borderBottomColor: '#b4b4b4b4b4'
  }
  const item = {
    fontSize: '12px',
    flexDirection: 'row',
    marginLeft: '15px',
    fontWeight: 'bold'
  }

  const buttonstyle = {
    marginLeft: '20px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '100px',
    borderRadius: '5px'
  }

  const handleRemove = async (id) => {
    axios
      .delete(`https://api.foodealzapi.com/Invendus/4/${id}`)
      .then(res => {
        console.log(id)
        if (res.data == 'basket Deleted!') {
          // handleCancel()
          getData()

        }
      })
      .catch((err) => {
        console.log(err);
      })
  }
  return (
    <div>
      <div style={Title1}>
        <h2>Baskets</h2>

      </div>

      <div style={buttonstyle}>
        <Link to= '/Basketsadd'>
        <Button variant="outline-success" >add Item </Button>
        </Link>
      </div>
      <div style={Search1} >
        <input type="text" placeholder=" Search Here..." value={q} onChange={(e) => setQ(e.target.value)} />
      </div>
      <div style={item}>
        <Table>
          <TableHead>
            <TableRow>
              <CustomTableCell>itemId</CustomTableCell>
              <CustomTableCell align="right">itemName</CustomTableCell>
              <CustomTableCell align="right">itemImage</CustomTableCell>
              <CustomTableCell align="right">PriceBeforeDiscount</CustomTableCell>
              <CustomTableCell align="right">PriceAfterDiscount</CustomTableCell>
              <CustomTableCell align="right">Discount</CustomTableCell>
              <CustomTableCell align="right">Discription</CustomTableCell>
              <CustomTableCell align="right">createAt</CustomTableCell>
              <CustomTableCell align="right">updatedAt</CustomTableCell>
              <CustomTableCell align="right">restaurantId</CustomTableCell>



            </TableRow>
          </TableHead>
          <TableBody>
            {data.data.map((item, id) => (
              <TableRow key={id}>
                <CustomTableCell>

                  {item.id}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.nom}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.image}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.PriceBeforeDiscount}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.PriceAfterDiscoun}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.discount}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.description}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.createdAt}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.updatedAt}
                </CustomTableCell>
                <CustomTableCell align="right">
                  {item.restaurant_id}
                </CustomTableCell>
                <CustomTableCell align="right">
                  <Link to= '/BasketsUpdate' >
                  <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success">
                    <EditIcon />
                  </Button>
                  </Link>
                </CustomTableCell>
                <CustomTableCell align="right">
                  <Button style={{ marginLeft: '20px', marginTop: '50px', backgroundColor: '#008037' }} variant="outline-success" onClick={ () => this.handleRemove(id)}>
                    <ClearIcon />
                  </Button>
                </CustomTableCell>
              </TableRow>
            ))}




          </TableBody>
        </Table>

      </div>
      <div>

      </div>

    </div>
  )
}
export default Baskets;
