import React, { useState, useEffect } from 'react';
import { TextField } from '@material-ui/core';
import { Button } from '@material-ui/core';
import {useForm} from 'react-hook-form';
import {yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup' ;
import { withStyles, makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';



const Title1 = {
    fontSize: '30px',
    color: '#008037',
    fontWeight: 'bold',
    marginLeft: '480px',
    justifyContent: 'center'
  }
  const Field = {
    marginLeft: '100px',
    width: '300px',
    marginTop: '25px',
    padding: '15px',
  
  
  }
  const buttonstyle = {
    marginLeft: '530px',
    marginTop: '50px',
    backgroundColor: '#008037',
    width: '120px',
    borderRadius: '5px'
  
  }
  
  let schema = yup.object().shape({
    username: yup.string().required( 'please enter username'),
    email: yup.string().email().required('email invallid'),
    password: yup.string().min(4).max(8).required('password nvalid'),
    // starting_date: yup.date().required('date invalid').nullable(),
    // expired_date: yup.date().required('date invalid').nullable(),
   
    // expired_hour: yup.string().required('hour invalid'),
   
  });

 

  const TestValide = () =>{

    const {register, handleSubmit, error} = useForm({
        resolver: yupResolver(schema),
      });

      const useStyles = makeStyles((theme) => ({
        ligne: {
          display: 'flex',
          flexDirection: 'row',
        },
        formControl: {
          margin: theme.spacing(1),
          minWidth: 200,
        },
        selectEmpty: {
          marginTop: theme.spacing(2),
        },
        container: {
          display: 'flex',
          flexWrap: 'wrap',
        },
        textField: {
          marginLeft: theme.spacing(1),
          marginRight: theme.spacing(1),
          width: 200,
        },
      }));

    //   const submitForm = (data) => {
    //     console.log('data input are :', data);
    //   };
      const classes = useStyles();
      return(
<div>

    <h2>Sign In</h2>
    <form >
        <div className={classes.ligne}>
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>username:</p>
        {/* <input type="text" name="username" placeholder="enter username"  ref={register}/> */}
        <TextField style={{ marginLeft: '20px', width: '300px', marginTop: '25px', padding: '15px' }}
          name="username"  variant="filled"  ref={register} />
        {/* <p>{error.username?.message}</p> */}
      </div>
      <div className={classes.ligne}>
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>email :</p>
        {/* <input type="text" name="email" placeholder="enter email"  ref={register}/> */}
        <TextField style={{ width: '300px', marginTop: '25px', padding: '15px' }}
          name="email"   variant="filled"  ref={register} />
          {/* <p>{error.email?.message}</p> */}
      </div>
      <div className={classes.ligne}>
        <p style={{ fontSize: '15px', color: "#008037", fontWeight: "bold", marginTop: '50px' }}>password :</p>
        {/* <input type="text" name="password" placeholder="password"  ref={register}/> */}
        <TextField style={{ width: '300px', marginTop: '25px', padding: '15px' }}
          name="password"   variant="filled"  ref={register} />
          {/* <p>{error.password?.message}</p> */}
      </div>
      </form>
      </div>

      );
  }
  export default TestValide;
